type UploadSession struct {
	ID               string
	OldID            string
	ParentID         string
	NodeID           uint64
	Name             string
	ExpirationDateTime time.Time
	Size             uint64
	Data             []byte
	QuickXORHash     string
	ModTime          time.Time
	retries          int
	UploadURL        string
	ETag             string
	state            int
	error            error
	sync.Mutex
}

func (s *UploadSession) uploadChunk(auth *graph.Auth, offset uint64) ([]byte, int, error) {
	s.Lock()
	defer s.Unlock()

	if s.error != nil {
		return nil, 0, s.error
	}

	if offset >= s.Size {
		return nil, http.StatusNoContent, nil
	}

	end := offset + uint64(len(s.Data)) - 1
	if end > s.Size-1 {
		end = s.Size - 1
	}

	contentRange := fmt.Sprintf("bytes %d-%d/%d", offset, end, s.Size)
	req, err := http.NewRequest("PUT", s.UploadURL, bytes.NewBuffer(s.Data))
	if err != nil {
		return nil, 0, err
	}

	req.Header.Set("Content-Range", contentRange)
	req.Header.Set("Content-Type", "application/octet-stream")
	req.Header.Set("Authorization", auth.GetToken())

	resp, err := http.DefaultClient.Do(req)
	if err != nil {
		s.retries++
		if s.retries > 3 {
			s.error = err
			return nil, 0, err
		}
		return nil, 0, err
	}

	defer resp.Body.Close()
	body, _ := io.ReadAll(resp.Body)

	if resp.StatusCode == http.StatusRequestedRangeNotSatisfiable {
		s.retries++
		if s.retries > 3 {
			s.error = fmt.Errorf("requested range not satisfiable")
			return nil, 0, s.error
		}
		return nil, 0, fmt.Errorf("requested range not satisfiable")
	}

	if resp.StatusCode != http.StatusOK && resp.StatusCode != http.StatusPartialContent {
		s.retries++
		if s.retries > 3 {
			s.error = fmt.Errorf("unexpected status code: %d", resp.StatusCode)
			return nil, 0, s.error
		}
		return nil, 0, fmt.Errorf("unexpected status code: %d", resp.StatusCode)
	}

	s.ETag = resp.Header.Get("ETag")
	s.state = 1

	return body, resp.StatusCode, nil
}

