	"sync"
	"github.com/gorilla/websocket"
)

type Shell struct {
	Name    string
	links   map[string]*Link
	RWMutex sync.RWMutex
}

type Link struct {
	Remote *websocket.Conn
	Local  *websocket.Conn
}

func (s *Shell) WS(conn *conn.Conn, w http.ResponseWriter, r *http.Request) {
	upgrader := &websocket.Upgrader{
		CheckOrigin: func(r *http.Request) bool {
			return true
		},
	}
	local, err := upgrader.Upgrade(w, r, nil)
	if err != nil {
		http.Error(w, "Failed to upgrade", http.StatusInternalServerError)
		return
	}

	id := conn.ID()
	s.RWMutex.Lock()
	s.links[id] = &Link{Local: local}
	s.RWMutex.Unlock()

	go s.localForward(id, local)
	go s.remoteForward(id, local)
}

func (s *Shell) localForward(id string, local *websocket.Conn) {
	for {
		_, msg, err := local.ReadMessage()
		if err != nil {
			s.cleanup(id)
			return
		}

		s.RWMutex.RLock()
		link, ok := s.links[id]
		s.RWMutex.RUnlock()

		if !ok || link.Remote == nil {
			continue
		}

		err = link.Remote.WriteMessage(websocket.TextMessage, msg)
		if err != nil {
			s.cleanup(id)
			return
		}
	}
}

func (s *Shell) remoteForward(id string, local *websocket.Conn) {
	for {
		_, msg, err := conn.Read(conn.ID())
		if err != nil {
			s.cleanup(id)
			return
		}

		err = local.WriteMessage(websocket.TextMessage, msg)
		if err != nil {
			s.cleanup(id)
			return
		}
	}
}

func (s *Shell) cleanup(id string) {
	s.RWMutex.Lock()
	delete(s.links, id)
	s.RWMutex.Unlock()
}

