import (
	"encoding/json"
	"fmt"
	"io/ioutil"
	"net/http"
	"strings"
	"time"

	"github.com/fireworq/fireworq/jobqueue"
	"github.com/rs/zerolog"
)

type HTTPWorker struct {
	UserAgent string
	Logger    *zerolog.Logger
}

func (worker *HTTPWorker) Work(job jobqueue.Job) *jobqueue.Result {
	client := &http.Client{
		Timeout: time.Duration(job.Timeout()) * time.Second,
	}
	req, err := http.NewRequest(
		"POST",
		job.URL(),
		strings.NewReader(job.Payload()),
	)
	if err != nil {
		return &jobqueue.Result{
			Status:  jobqueue.ResultStatusInternalFailure,
			Message: fmt.Sprintf("Cannot create http request: %v", err),
		}
	}
	req.Header.Add("Content-Type", "application/json")

	userAgent := worker.UserAgent
	if userAgent == "" {
		userAgent = defaultUserAgent
	}
	req.Header.Add("User-Agent", userAgent)

	resp, err := client.Do(req)

	worker.Logger.Debug().
		Str("action", "dispatch").
		Str("worker", "HTTPWorker").
		Str("url", job.URL()).
		Str("payload", job.Payload()).
		Msg("Dispatched via HTTP")

	if err != nil {
		return &jobqueue.Result{
			Status:  jobqueue.ResultStatusInternalFailure,
			Message: fmt.Sprintf("Request failed: %v", err),
		}
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return &jobqueue.Result{
			Status:  jobqueue.ResultStatusFailure,
			Code:    resp.StatusCode,
			Message: fmt.Sprintf("Cannot read body: %v", err),
		}
	}

	var rslt jobqueue.Result
	err = json.Unmarshal(body, &rslt)
	if err != nil {
		return &jobqueue.Result{
			Status: jobqueue.ResultStatusFailure,
			Code:   resp.StatusCode,
			Message: fmt.Sprintf(
				"Cannot parse body as JSON: %v\nOriginal response body:\n%s",
				err,
				string(body),
			),
		}
	}

	if !rslt.IsValid() {
		return &jobqueue.Result{
			Status:  jobqueue.ResultStatusFailure,
			Code:    resp.StatusCode,
			Message: fmt.Sprintf("Invalid result status: %s\nOriginal response body:\n%s", rslt.Status, string(body)),
		}
	}

	rslt.Code = resp.StatusCode
	return &rslt
}

import (
	"context"
	"fmt"
	"io"
	"net"
	"net/http"
	"golang.org/x/sync/errgroup"
	"github.com/miekg/dns"
)

type localNetworkServices struct {
	domain   string
	webpages map[string]string
	ipAddr   string
}

func (l localNetworkServices) DNSServerIP() string {
	return l.ipAddr
}

func (l localNetworkServices) URL(subpath string) string {
	return fmt.Sprintf("http://%s/%s", l.domain, subpath)
}

func (l localNetworkServices) Serve(ctx context.Context) error {
	errGroup, _ := errgroup.WithContext(ctx)

	errGroup.Go(func() error {
		dnsSrv := &dns.Server{
			Addr: l.ipAddr + ":53",
			Net:  "udp",
			Handler: &dnsHandler{
				records: map[string]string{
					l.domain + ".": l.ipAddr,
				},
			},
		}
		return dnsSrv.ListenAndServe()
	})

	errGroup.Go(func() error {
		for path, contents := range l.webpages {
			webpage := contents
			http.HandleFunc("/"+path, func(w http.ResponseWriter, r *http.Request) {
				io.WriteString(w, webpage)
			})
		}

		return http.ListenAndServe(l.ipAddr+":80", nil)
	})

	return errGroup.Wait()
}

type dnsHandler struct {
	records map[string]string
}

func (h dnsHandler) ServeDNS(w dns.ResponseWriter, r *dns.Msg) {
	msg := dns.Msg{}
	msg.SetReply(r)
	if r.Question[0].Qtype == dns.TypeA {
		msg.Authoritative = true
		domain := msg.Question[0].Name
		address, ok := h.records[domain]
		if ok {
			msg.Answer = append(msg.Answer, &dns.A{
				Hdr: dns.RR_Header{Name: domain, Rrtype: dns.TypeA, Class: dns.ClassINET, Ttl: 3600},
				A:   net.ParseIP(address),
			})
		} else {
			msg.SetRcode(r, dns.RcodeNameError)
		}
	}

	w.WriteMsg(&msg)
}

