type Api struct {
	port         int
	bindIp       string
	tlsCertFile  string
	tlsKeyFile   string
	timeout      time.Duration
	rootPath     string
	srv          *echo.Echo
	server       *http.Server
	listenAddr   string
	shutdownChan chan struct{}
}

func NewApi(port int, bindIp, rootPath string, timeout time.Duration) *Api {
	e := echo.New()
	e.Use(middleware.Logger())
	e.Use(middleware.Recover())

	e.GET(rootPath, func(c echo.Context) error {
		return c.String(http.StatusOK, "Welcome to the API")
	})

	return &Api{
		port:         port,
		bindIp:       bindIp,
		rootPath:     rootPath,
		timeout:      timeout,
		srv:          e,
		shutdownChan: make(chan struct{}),
	}
}

func (a *Api) Start() error {
	a.listenAddr = net.JoinHostPort(a.bindIp, strconv.Itoa(a.port))

	cfg := &tls.Config{}
	if a.tlsCertFile != "" && a.tlsKeyFile != "" {
		cert, err := tls.LoadX509KeyPair(a.tlsCertFile, a.tlsKeyFile)
		if err != nil {
			return err
		}
		cfg = &tls.Config{Certificates: []tls.Certificate{cert}, MinVersion: tls.VersionTLS12}
	}

	server := &http.Server{
		Addr:         a.listenAddr,
		Handler:      a.srv,
		ReadTimeout:  a.timeout,
		WriteTimeout: a.timeout,
		TLSConfig:    cfg,
	}

	a.server = server

	go func() {
		var err error
		if a.tlsCertFile != "" && a.tlsKeyFile != "" {
			err = server.ListenAndServeTLS("", "")
		} else {
			err = server.ListenAndServe()
		}
		if err != nil && err != http.ErrServerClosed {
			log.Fatalf("Server failed: %v", err)
		}
		close(a.shutdownChan)
	}()

	return nil
}

func (a *Api) Stop(ctx context.Context) error {
	if a.server == nil {
		return nil
	}

	return a.server.Shutdown(ctx)
}
