package main

import (
	"fmt"
	"io"
	"net/http"

	"github.com/labstack/echo/v4"
	"github.com/labstack/echo/v4/middleware"
)

type mjHandler struct {
	log                    echo.Logger
	tenhouMessageReceiver  *tenhou.MessageReceiver
	majsoulMessageQueue    chan []byte
}

func (h *mjHandler) analysisTenhou(c echo.Context) error {
	body, err := io.ReadAll(c.Request().Body)
	if err != nil {
		h.log.Errorf("Failed to read request body: %v", err)
		return c.String(http.StatusBadRequest, "Invalid request")
	}
	if err := h.tenhouMessageReceiver.Receive(body); err != nil {
		h.log.Errorf("Failed to process Tenhou message: %v", err)
		return c.String(http.StatusInternalServerError, "Processing error")
	}
	return c.String(http.StatusOK, "Tenhou message processed")
}

func (h *mjHandler) analysisMajsoul(c echo.Context) error {
	body, err := io.ReadAll(c.Request().Body)
	if err != nil {
		h.log.Errorf("Failed to read request body: %v", err)
		return c.String(http.StatusBadRequest, "Invalid request")
	}
	select {
	case h.majsoulMessageQueue <- body:
	default:
		h.log.Warn("Majsoul message queue is full, dropping message")
	}
	return c.String(http.StatusOK, "Majsoul message queued")
}

func runServer(isHTTPS bool, port int) error {
	e := echo.New()
	e.Use(middleware.Logger())
	e.Use(middleware.Recover())

	log := e.Logger

	tenhouReceiver := &tenhou.MessageReceiver{}
	majsoulQueue := make(chan []byte, 100)

	handler := &mjHandler{
		log:                    log,
		tenhouMessageReceiver: tenhouReceiver,
		majsoulMessageQueue:   majsoulQueue,
	}

	e.POST("/tenhou", handler.analysisTenhou)
	e.POST("/majsoul", handler.analysisMajsoul)

	var err error
	if isHTTPS {
		err = e.StartTLSPort(fmt.Sprintf(":%d", port), "cert.pem", "key.pem")
	} else {
		err = e.Start(fmt.Sprintf(":%d", port))
	}

	return err
}

