package dashboard

import (
	"html/template"
	"net/http"
	"runtime"
	"time"

	"github.com/gofiber/fiber/v2"
	"github.com/gofiber/fiber/v2/middleware/basicauth"
	"github.com/gofiber/fiber/v2/middleware/logger"
)

type Dashboard struct {
	App             *fiber.App
	queue           models.Queue
	tenantManager   models.TenantManager
	cfg             config.DashboardConfig
}

func NewDashboard(queue models.Queue, tenantManager models.TenantManager, cfg config.DashboardConfig) *Dashboard {
	app := fiber.New(fiber.Config{
		BodyLimit: 10 * 1024 * 1024,
	})

	app.Settings().Template = template.Must(template.ParseGlob("templates/*.html"))

	if cfg.LoggerEnabled {
		app.Use(logger.New(logger.Config{
			Format:     "${time} ${status} - ${method} ${path}\n",
			TimeFormat: "02-Jan-2006 15:04:05",
		}))
	}

	if cfg.BasicAuthEnabled {
		app.Use(basicauth.New(basicauth.Config{
			Users: map[string]string{
				cfg.BasicAuthUsername: cfg.BasicAuthPassword,
			},
		}))
	}

	dashboard := &Dashboard{
		App:             app,
		queue:           queue,
		tenantManager:   tenantManager,
		cfg:             cfg,
	}

	app.Get("/", dashboard.Queues)
	app.Post("/queues", dashboard.NewQueue)
	app.Get("/queues/:queue", dashboard.Queue)
	app.Get("/queues/:queue/settings", dashboard.QueueSettings)
	app.Post("/queues/:queue/settings", dashboard.SaveQueueSettings)
	app.Get("/queues/:queue/delete", dashboard.DeleteQueueConfirm)
	app.Post("/queues/:queue/delete", dashboard.DeleteQueue)
	app.Get("/queues/:queue/messages/:message", dashboard.Message)

	return dashboard
}

func (d *Dashboard) Start() error {
	if !d.cfg.Enabled {
		return nil
	}

	addr := d.cfg.Address
	if addr == "" {
		addr = ":8080"
	}

	return d.App.Listen(addr)
}

func (d *Dashboard) Stop() error {
	if !d.cfg.Enabled {
		return nil
	}

	ctx, cancel := context.WithTimeout(context.Background(), 10*time.Second)
	defer cancel()

	return d.App.Shutdown(ctx)
}

func (d *Dashboard) Queues(c *fiber.Ctx) error {
	queues, err := d.queue.List()
	if err != nil {
		return c.Status(fiber.StatusInternalServerError).SendString("Failed to list queues")
	}

	return c.Render("dashboard", fiber.Map{
		"Queues": queues,
	})
}

func (d *Dashboard) NewQueue(c *fiber.Ctx) error {
	name := c.FormValue("name")
	if name == "" {
		return c.Status(fiber.StatusBadRequest).SendString("Queue name is required")
	}

	if err := d.queue.Create(name); err != nil {
		return c.Status(fiber.StatusInternalServerError).SendString("Failed to create queue")
	}

	return c.Redirect("/queues/" + name)
}

func (d *Dashboard) Queue(c *fiber.Ctx) error {
	queueName := c.Params("queue")
	queue, err := d.queue.Get(queueName)
	if err != nil {
		return c.Status(fiber.StatusNotFound).SendString("Queue not found")
	}

	return c.Render("queue", fiber.Map{
		"Queue": queue,
	})
}

func (d *Dashboard) QueueSettings(c *fiber.Ctx) error {
	queueName := c.Params("queue")
	queue, err := d.queue.Get(queueName)
	if err != nil {
		return c.Status(fiber.StatusNotFound).SendString("Queue not found")
	}

	return c.Render("queue_settings", fiber.Map{
		"Queue": queue,
	})
}

func (d *Dashboard) SaveQueueSettings(c *fiber.Ctx) error {
	queueName := c.Params("queue")
	settings := struct {
		MaxSize int
		Timeout time.Duration
	}{}

	if err := c.BodyParser(&settings); err != nil {
