	queueName := c.Params("queue")
	queue, err := d.queue.Get(queueName)
	if err != nil {
		return c.Status(fiber.StatusNotFound).SendString("Queue not found")
	}

	return c.Render("delete_queue_confirm", fiber.Map{
		"Queue": queue,
	})
}

func (d *Dashboard) DeleteQueue(c *fiber.Ctx) error {
	queueName := c.Params("queue")
	if err := d.queue.Delete(queueName); err != nil {
		return c.Status(fiber.StatusInternalServerError).SendString("Failed to delete queue")
	}

	return c.Redirect("/")
}

func (d *Dashboard) Message(c *fiber.Ctx) error {
	queueName := c.Params("queue")
	messageID := c.Params("message")
	message, err := d.queue.GetMessage(queueName, messageID)
	if err != nil {
		return c.Status(fiber.StatusNotFound).SendString("Message not found")
	}

	return c.Render("message", fiber.Map{
		"Message": message,
	})
}

