import (
	"github.com/gin-gonic/gin"
	"net/http"
	"sync"
	"time"
)

func setupServer() {
	r := gin.Default()

	var (
		cache struct {
			value     string
			expiresAt time.Time
			mu        sync.Mutex
		}
	)

	r.GET("/cache_ping", func(c *gin.Context) {
		now := time.Now()
		cache.mu.Lock()
		if !cache.expiresAt.IsZero() && now.Before(cache.expiresAt) {
			c.String(http.StatusOK, cache.value)
			cache.mu.Unlock()
			return
		}
		cache.mu.Unlock()

		newValue := "pong " + now.Format(time.RFC3339)
		cache.mu.Lock()
		cache.value = newValue
		cache.expiresAt = now.Add(1 * time.Minute)
		cache.mu.Unlock()

		c.String(http.StatusOK, newValue)
	})

	r.Run(":8080")
}

