)

func getBackendAddress() (string, error) {
	return "http://localhost:8081", nil
}

func main() {
	r := gin.Default()
	r.GET("/:path", func(c *gin.Context) {
		path := c.Param("path")
		backendURL, err := getBackendAddress()
		if err != nil {
			log.Println("Failed to get backend address:", err)
			c.AbortWithStatusJSON(500, gin.H{"error": "Failed to get backend address"})
			return
		}

		url := fmt.Sprintf("%s/%s", backendURL, path)

		bodyBytes, err := io.ReadAll(c.Request.Body)
		if err != nil {
			log.Println("Failed to read request body:", err)
			c.AbortWithStatusJSON(500, gin.H{"error": "Failed to read request body"})
			return
		}
		c.Request.Body.Close()

		req, err := http.NewRequest(c.Request.Method, url, bytes.NewBuffer(bodyBytes))
		if err != nil {
			log.Println("Failed to create request:", err)
			c.AbortWithStatusJSON(500, gin.H{"error": "Failed to create request"})
			return
		}

		for key, values := range c.Request.Header {
			for _, value := range values {
				req.Header.Add(key, value)
			}
		}

		client := &http.Client{}
		resp, err := client.Do(req)
		if err != nil {
			log.Println("Failed to forward request to backend:", err)
			c.AbortWithStatusJSON(500, gin.H{"error": "Failed to forward request to backend"})
			return
		}
		defer resp.Body.Close()

		for key, values := range resp.Header {
			for _, value := range values {
				c.Header(key, value)
			}
		}

		c.Status(resp.StatusCode)

		_, err = io.Copy(c.Writer, resp.Body)
		if err != nil {
			log.Println("Failed to stream response body:", err)
			c.AbortWithStatusJSON(500, gin.H{"error": "Failed to stream response body"})
			return
		}
	})

	r.Run(":8080")
}

