package main

import (
    "fmt"
    "net/http"
    "sync"
    "time"

    "github.com/gin-gonic/gin"
)

type Client struct {
    lastTime time.Time
    tokens   int
}

type RateLimiter struct {
    maxRPS int
    clients map[string]*Client
    mu      sync.Mutex
}

func min(a, b int) int {
    if a < b {
        return a
    }
    return b
}

func ginRun(rps int) {
    fmt.Printf("Rate limit set to %d RPS\n", rps)

    limiter := &RateLimiter{
        maxRPS:  rps,
        clients: make(map[string]*Client),
    }

    r := gin.Default()

    r.Use(func(c *gin.Context) {
        clientIP := c.ClientIP()

        limiter.mu.Lock()
        client, exists := limiter.clients[clientIP]
        if !exists {
            client = &Client{
                lastTime: time.Now(),
                tokens:   rps,
            }
            limiter.clients[clientIP] = client
        }

        elapsed := time.Since(client.lastTime)
        tokensToAdd := int(elapsed.Seconds()) * limiter.maxRPS
        client.tokens = min(client.tokens + tokensToAdd, limiter.maxRPS)

        if client.tokens >= 1 {
            client.tokens--
            client.lastTime = time.Now()
            limiter.mu.Unlock()
            c.Next()
        } else {
            limiter.mu.Unlock()
            c.AbortWithStatusJSON(http.StatusTooManyRequests, gin.H{"error": "Rate limit exceeded"})
        }
    })

    r.GET("/rate", func(c *gin.Context) {
        c.JSON(http.StatusOK, gin.H{"message": "Request allowed", "rps": rps})
    })

    r.Run(":8080")
}

