		if err != nil {
			c.JSON(400, gin.H{
				"status":  1,
				"message": "Error retrieving the file",
				"error":   err.Error(),
			})
			return
		}

		// Generate unique filename
		ext := getFileExtension(file.Filename)
		uniqueName := generateUniqueFilename(ext)

		// Save the file to the specified directory
		dst := fmt.Sprintf("/root/file/%s", uniqueName)
		if err := c.SaveUploadedFile(file, dst); err != nil {
			c.JSON(500, gin.H{
				"status":  2,
				"message": "Error saving the file",
				"error":   err.Error(),
			})
			return
		}

		// Construct the URL
		fileURL := fmt.Sprintf("http://localhost:8085/files/%s", uniqueName)

		// Return success response
		c.JSON(200, gin.H{
			"status":  0,
			"message": "File uploaded successfully",
			"data": gin.H{
				"url": fileURL,
			},
		})
	})

	// Start the server on port 8085
	r.Run(":8085")
}

func getFileExtension(filename string) string {
	ext := ""
	for i := len(filename) - 1; i >= 0; i-- {
		if filename[i] == '.' {
			ext = filename[i:]
			break
		}
	}
	return ext
}

func generateUniqueFilename(extension string) string {
	timestamp := time.Now().UnixNano()
	randomStr := fmt.Sprintf("%06d", rand.Intn(1000000))
	return fmt.Sprintf("%d-%s%s", timestamp, randomStr, extension)
}

