package main

import (
	"fmt"
	"io/ioutil"
	"net/http"
	"net/url"
	"os"
	"strings"

	"github.com/gin-gonic/gin"
)

type Config struct {
	IP   string
	Port string
	Proxy string
	Token string
}

func initConfig() *Config {
	return &Config{
		IP:    os.Getenv("IP"),
		Port:  os.Getenv("PORT"),
		Proxy: os.Getenv("PROXY"),
		Token: os.Getenv("TOKEN"),
	}
}

func main() {
	config := initConfig()

	var client *http.Client
	if config.Proxy != "" {
		proxyURL, _ := url.Parse(config.Proxy)
		transport := &http.Transport{
			Proxy: http.ProxyURL(proxyURL),
		}
		client = &http.Client{Transport: transport}
	} else {
		client = &http.Client{}
	}

	r := gin.Default()
	r.Use(corsMiddleware())

	r.GET("/", func(c *gin.Context) {
		c.JSON(http.StatusOK, gin.H{
			"ip":   config.IP,
			"port": config.Port,
			"proxy": config.Proxy,
		})
	})

	r.POST("/translate", authMiddleware(config.Token), func(c *gin.Context) {
		var req struct {
			Text         string `json:"text"`
			SourceLang   string `json:"source_lang"`
			TargetLang   string `json:"target_lang"`
		}
		if err := c.ShouldBindJSON(&req); err != nil {
			c.AbortWithStatusJSON(http.StatusBadRequest, gin.H{"error": "Invalid request"})
			return
		}

		apiURL := "https://api.deepl.com/v2/translate"
		if config.Proxy != "" {
			apiURL = config.Proxy + "/translate"
		}

		payload := fmt.Sprintf("text=%s&source_lang=%s&target_lang=%s", req.Text, req.SourceLang, req.TargetLang)
		reqBody := strings.NewReader(payload)
		httpReq, _ := http.NewRequest("POST", apiURL, reqBody)
		httpReq.Header.Set("Content-Type", "application/x-www-form-urlencoded")
		httpReq.Header.Set("Authorization", "DeepL-Auth-Key "+config.Token)

		resp, err := client.Do(httpReq)
		if err != nil {
			c.AbortWithStatusJSON(http.StatusInternalServerError, gin.H{"error": "API request failed"})
			return
		}
		defer resp.Body.Close()

		body, _ := ioutil.ReadAll(resp.Body)
		c.Data(resp.StatusCode, "application/json", body)
	})

	fmt.Printf("Server started at http://%s:%s\n", config.IP, config.Port)
	fmt.Printf("Config: IP=%s, Port=%s, Proxy=%s\n", config.IP, config.Port, config.Proxy)
	r.Run(fmt.Sprintf("%s:%s", config.IP, config.Port))
}

func corsMiddleware() gin.HandlerFunc {
	return func(c *gin.Context) {
		c.Writer.Header().Set("Access-Control-Allow-Origin", "*")
		c.Writer.Header().Set("Access-Control-Allow-Methods", "GET, POST, OPTIONS")
		c.Writer.Header().Set("Access-Control-Allow-Headers", "Content-Type, Authorization")
		if c.Request.Method == "OPTIONS" {
			c.AbortWithStatus(204)
			return
		}
		c.Next()
	}
}

func authMiddleware(token string) gin.HandlerFunc {
	return func(c *gin.Context) {
		authHeader := c.GetHeader("Authorization")
		if authHeader != "Bearer "+token {
			c.AbortWithStatusJSON(http.StatusUnauthorized, gin.H{"error": "Unauthorized"})
			return
		}
		c.Next()
	}
}

