		rsp.Status = err.Error()
		return nil
	}
	defer conn.Close()

	if req.Data != "" {
		_, err := conn.Write([]byte(req.Data))
		if err != nil {
			rsp.Status = err.Error()
			return nil
		}
	}

	reader := bufio.NewReader(conn)
	data, err := reader.ReadBytes('\x00')
	if err != nil && err != io.EOF {
		rsp.Status = err.Error()
		return nil
	}
	rsp.Data = string(data)
	rsp.Status = "OK"
	return nil
}

