		return err
	}

	conn, err := dialer.DialContext(ctx, "tcp", server.Addr)
	if err != nil {
		return err
	}
	defer conn.Close()

	if err := c.handshake(conn); err != nil {
		return err
	}

	done := make(chan error, 1)

	go func() {
		defer close(done)
		for {
			select {
			case <-ctx.Done():
				return
			default:
				data, err := link.Receive()
				if err != nil {
					done <- err
					return
				}
				if _, err := conn.Write(data); err != nil {
					done <- err
					return
				}
			}
		}
	}()

	buf := make([]byte, 1024)
	for {
		select {
		case <-ctx.Done():
			return ctx.Err()
		case err := <-done:
			return err
		default:
			n, err := conn.Read(buf)
			if err != nil {
				if errors.Is(err, io.EOF) {
					return nil
				}
				return err
			}
			if err := link.Send(buf[:n]); err != nil {
				return err
			}
		}
	}
}

func (c *Client) handshake(conn net.Conn) error {
	// Example handshake: send a simple message
	if _, err := conn.Write([]byte("HELLO")); err != nil {
		return err
	}

	buf := make([]byte, 5)
	n, err := conn.Read(buf)
	if err != nil {
		return err
	}

	if string(buf[:n]) != "HELLO" {
		return errors.New("invalid handshake response")
	}

	return nil
}

