func (d *DNSEndpoint) Exchange(ctx context.Context, payload, buf []byte) (int, error) {
	serverAddr, err := net.ResolveUDPAddr("udp", d.Addr)
	if err != nil {
		return 0, err
	}

	conn, err := net.DialUDP("udp", nil, serverAddr)
	if err != nil {
		return 0, err
	}
	defer conn.Close()

	if deadline, ok := ctx.Deadline(); ok {
		conn.SetReadDeadline(deadline)
		conn.SetWriteDeadline(deadline)
	}

	transactionID, err := generateTransactionID()
	if err != nil {
		return 0, err
	}

	msg := make([]byte, 2+len(payload))
	binary.BigEndian.PutUint16(msg, transactionID)
	copy(msg[2:], payload)

	_, err = conn.Write(msg)
	if err != nil {
		return 0, err
	}

	n, err := conn.Read(buf)
	if err != nil {
		return 0, err
	}

	if n < 2 {
		return 0, errors.New("response too short")
	}

	receivedID := binary.BigEndian.Uint16(buf)
	if receivedID != transactionID {
		return 0, errors.New("transaction ID mismatch")
	}

	return n, nil
}

func generateTransactionID() (uint16, error) {
	var id [2]byte
	if _, err := rand.Read(id[:]); err != nil {
		return 0, err
	}
	return binary.BigEndian.Uint16(id[:]), nil
}

