	timeout := c.getTimeoutForRequest(c.Timeout)

	if c.SingleInflight {
		var resp *dns.Msg
		var duration time.Duration
		var err error
		key := fmt.Sprintf("%s-%v", address, m)
		c.group.Do(key, func() (interface{}, error) {
			r, d, e := c.exchange(m, address)
			resp = r
			duration = d
			err = e
			return resp, err
		})
		return resp, duration, err
	}

	return c.exchange(m, address)
}

func (c *Client) exchange(m *dns.Msg, a string) (*dns.Msg, time.Duration, error) {
	start := time.Now()

	conn, err := c.Dial(a)
	if err != nil {
		return nil, 0, err
	}
	defer conn.Close()

	if err := conn.WriteMsg(m); err != nil {
		return nil, 0, err
	}

	resp, err := conn.ReadMsg()
	if err != nil {
		return nil, 0, err
	}

	duration := time.Since(start)
	return resp, duration, nil
}

func (c *Client) getTimeoutForRequest(timeout time.Duration) time.Duration {
	if timeout > 0 {
		return timeout
	}
	return c.DialTimeout + c.ReadTimeout + c.WriteTimeout
}

func (conn *Conn) WriteMsg(m *dns.Msg) error {
	return m.WriteTo(conn.Conn)
}

func (conn *Conn) ReadMsg() (*dns.Msg, error) {
	msg := new(dns.Msg)
	return msg, msg.ReadFrom(conn.Conn)
}

