package main

import (
    "fmt"
    "net"
    "os"
    "syscall"
)

func main() {
    listener, err := net.Listen("tcp", ":1024")
    if err != nil {
        panic(err)
    }
    defer listener.Close()

    for {
        conn, err := listener.Accept()
        if err != nil {
            fmt.Println("Accept error:", err)
            continue
        }
        go handleConnection(conn)
    }
}

func handleConnection(conn net.Conn) {
    defer conn.Close()

    remoteAddr, ok := conn.RemoteAddr().(*net.TCPAddr)
    if !ok {
        return
    }
    isIPv6 := remoteAddr.IP.To4() == nil

    if isIPv6 {
        tcpConn, ok := conn.(*net.TCPConn)
        if !ok {
            return
        }
        file, err := tcpConn.File()
        if err != nil {
            return
        }
        fd := int(file.Fd())

        trafficClass := 0x20
        if err := syscall.SetsockoptInt(fd, syscall.IPPROTO_IPV6, syscall.IPV6_TCLASS, int(trafficClass)); err != nil {
            fmt.Println("Set traffic class error:", err)
        }

        hopLimit := 64
        if err := syscall.SetsockoptInt(fd, syscall.IPPROTO_IPV6, syscall.IPV6_HOPLIMIT, hopLimit); err != nil {
            fmt.Println("Set hop limit error:", err)
        }
    }

    _, err := conn.Write([]byte("HELLO-R-U-THERE-ACK"))
    if err != nil {
        fmt.Println("Write error:", err)
    }
}

