package gitdaemon

import (
	"context"
	"fmt"
	"log"
	"net"
	"sync"
	"time"

	"github.com/golang/glog"
	"golang.org/x/sync/errgroup"
)

type GitDaemon struct {
	ctx      context.Context
	addr     string
	finished chan struct{}
	conns    connections
	cfg      *config.Config
	be       *backend.Backend
	wg       sync.WaitGroup
	once     sync.Once
	logger   *log.Logger
	done     atomic.Bool
}

type serverConn struct {
	net.Conn
}

type connections struct {
	mu sync.Map
}

func NewGitDaemon(ctx context.Context) (*GitDaemon, error) {
	cfg := config.NewDefaultConfig()
	be := backend.NewBackend(cfg)
	logger := log.New(log.Writer(), "[git-daemon] ", log.LstdFlags)
	return &GitDaemon{
		ctx:      ctx,
		addr:     "tcp://:9418",
		finished: make(chan struct{}),
		conns:    connections{},
		cfg:      cfg,
		be:       be,
		logger:   logger,
	}, nil
}

func (d *GitDaemon) ListenAndServe() error {
	if err := d.startServer(); err != nil {
		return err
	}
	<-d.finished
	return nil
}

func (d *GitDaemon) startServer() error {
	var eg errgroup.Group
	eg.Go(func() error {
		listener, err := net.Listen("tcp", d.addr)
		if err != nil {
			return err
		}
		defer listener.Close()
		d.logger.Printf("Listening on %s", d.addr)
		for {
			select {
			case <-d.ctx.Done():
				return nil
			default:
				conn, err := listener.Accept()
				if err != nil {
					if netErr, ok := err.(net.Error); ok && netErr.Temporary() {
						time.Sleep(time.Second)
						continue
					}
					return err
				}
				sc := &serverConn{Conn: conn}
				d.conns.mu.Store(conn, struct{}{})
				d.wg.Add(1)
				go func() {
					defer d.wg.Done()
					d.handleClient(sc)
				}()
			}
		}
	})
	return eg.Wait()
}

func (d *GitDaemon) handleClient(sc *serverConn) {
	defer func() {
		sc.Close()
		d.conns.mu.Delete(sc.Conn)
	}()

	// Set initial timeout
	if err := sc.SetDeadline(time.Now().Add(d.cfg.MaxTimeout)); err != nil {
		d.logger.Printf("Failed to set deadline: %v", err)
		return
	}

	// Read service name
	service, err := readService(sc)
	if err != nil {
		d.logger.Printf("Failed to read service: %v", err)
		return
	}

	// Validate service
	if service != "git-upload-pack" && service != "git-upload-archive" {
		sendError(sc, "Service not allowed")
		return
	}

	// Read repository path
	repoPath, err := readRepoPath(sc)
	if err != nil {
		d.logger.Printf("Failed to read repository path: %v", err)
		return
	}

	// Check access
	if !d.be.CheckAccess(repoPath) {
		sendError(sc, "Access denied")
		return
	}

	// Process service
	switch service {
	case "git-upload-pack":
		d.be.UploadPack(repoPath, sc)
	case "git-upload-archive":
		d.be.UploadArchive(repoPath, sc)
	}
}

func (d *GitDaemon) Serve(listener net.Listener) error {
	d.logger.Printf("Serving on %s", listener.Addr())
	for {
		select {
		case <-d.ctx.Done():
			return nil
		default:
			conn, err := listener.Accept()
			if err != nil {
				if netErr, ok := err.(net.Error); ok && netErr.Temporary() {
					time.Sleep(time.Second)
					continue
				}
				return err
			}
			sc := &serverConn{Conn: conn}
			d.conns.mu.Store(conn, struct{}{})
			d.wg.Add(1)
			go func() {
				defer d.wg.Done()
				d.handleClient(sc)
			}()
		}
	}
}

func (d *GitDaemon) Shutdown() {
	d.once.Do(func() {
		close(d.finished)
		d.wg.Wait()
		d.done.Store(true)
	})
}

func sendError(conn net.Conn, msg string) {
	_, _ = fmt.Fprintf(conn, "0000%04o\n", 12)
	_, _ = fmt.Fprintf(conn, "0004%s\n", msg)
}

func readService(conn net.Conn) (string, error) {
	buf := make([]byte, 1024)
	n, err := conn.Read(buf)
	if err != nil {
		return "", err
	}

	if n < 4 {
		return "", fmt.Errorf("invalid packet length")
	}

	length := int(buf[0])<<8 | int(buf[1])
	if length == 0 {
		return "", fmt.Errorf("invalid service packet")
	}

	if n < length+4 {
		return "", fmt.Errorf("incomplete packet")
	}

	return string(buf[4 : 4+length]), nil
}

func readRepoPath(conn net.Conn) (string, error) {
	buf := make([]byte, 1024)
	n, err := conn.Read(buf)
	if err != nil {
		return "", err
	}

	if n < 4 {
		return "", fmt.Errorf("invalid packet length")
	}

	length := int(buf[0])<<8 | int(buf[1])
	if length == 0 {
		return "", fmt.Errorf("invalid repository path packet")
	}

	if n < length+4 {
		return "", fmt.Errorf("incomplete packet")
	}

	return string(buf[4 : 4+length]), nil
}
