func (srv *Server) ListenAndServe() error {
	unlock := unlockOnce(&srv.lock)
	srv.lock.Lock()
	defer unlock()

	if srv.started {
		return &Error{err: "server already started"}
	}

	addr := srv.Addr
	if addr == "" {
		addr = ":domain"
	}

	srv.init()

	switch srv.Net {
	case "tcp", "tcp4", "tcp6":
		l, err := net.Listen(srv.Net, addr)
		if err != nil {
			return err
		}
		srv.Listener = l
		srv.started = true
		unlock()
		return srv.serveTCP(l)
	}
	return &Error{err: "bad network"}
}

func (srv *Server) serveTCP(l net.Listener) error {
	defer l.Close()

	var wg sync.WaitGroup
	defer func() {
		wg.Wait()
		close(srv.shutdown)
	}()

	for srv.isStarted() {
		rw, err := l.Accept()
		if err != nil {
			if !srv.isStarted() {
				return nil
			}
			if neterr, ok := err.(net.Error); ok && neterr.Temporary() {
				continue
			}
			return err
		}
		srv.lock.Lock()
		srv.conns[rw] = struct{}{}
		srv.lock.Unlock()
		wg.Add(1)
		go srv.serveTCPConn(&wg, rw)
	}

	return nil
}

func (srv *Server) serveTCPConn(wg *sync.WaitGroup, rw net.Conn) {
	defer wg.Done()

	w := &response{tcp: rw}
	w.writer = w

	timeout := srv.getReadTimeout()
	idleTimeout := srv.getIdleTimeout()

	for srv.isStarted() {
		m, err := srv.readTCP(rw, timeout)
		if err != nil {
			break
		}
		srv.serveDNS(m, w)
		if w.closed {
			break
		}
