		return err
	}
	ln, err := net.Listen(s.Net, s.Addr)
	if err != nil {
		return err
	}
	s.started = true
	return s.serveTCP(ln)
}

func (s *Server) serveTCP(ln net.Listener) error {
	defer ln.Close()
	var wg sync.WaitGroup
	for {
		select {
		case <-s.shutdown:
			return nil
		default:
			conn, err := ln.Accept()
			if err != nil {
				if netErr, ok := err.(net.Error); ok && netErr.Temporary() {
					time.Sleep(1 * time.Second)
					continue
				}
				return err
			}
			s.lock.Lock()
			s.conns[conn] = struct{}{}
			s.lock.Unlock()
			wg.Add(1)
			go func() {
				s.serveTCPConn(&wg, conn)
				wg.Done()
			}()
		}
	}
}

func (s *Server) serveTCPConn(wg *sync.WaitGroup, conn net.Conn) {
	defer conn.Close()
	defer wg.Done()
	conn.SetReadDeadline(time.Now().Add(s.getReadTimeout()))
	conn.SetWriteDeadline(time.Now().Add(s.getWriteTimeout()))
	idleDuration := s.getIdleTimeout()
	timer := time.AfterFunc(idleDuration, func() {
		s.lock.Lock()
		delete(s.conns, conn)
		s.lock.Unlock()
		conn.Close()
	})
	for {
		data, err := s.readTCP(conn, s.getReadTimeout())
		if err != nil {
			break
		}
		if err := s.Handler.Serve(conn); err != nil {
			break
		}
		timer.Stop()
		idleDuration := s.getIdleTimeout()
		timer = time.AfterFunc(idleDuration, func() {
			s.lock.Lock()
			delete(s.conns, conn)
			s.lock.Unlock()
			conn.Close()
		})
	}
}

