func NewServer(addr string, nodeID int32, partitionCount int32) (*Server, error) {
	listener, err := net.Listen("tcp", addr)
	if err != nil {
		return nil, err
	}
	s := &Server{
		Addr:         listener.Addr(),
		closing:      make(chan struct{}),
		nodeID:       nodeID,
		partitionCount: partitionCount,
	}
	s.prepareBrokerMsg()
	s.wg.Add(1)
	go s.run(listener)
	return s, nil
}

func (s *Server) prepareBrokerMsg() {
	var buf bytes.Buffer
	binary.Write(&buf, binary.BigEndian, s.nodeID)
	binary.Write(&buf, binary.BigEndian, s.partitionCount)
	s.brokerMessage = buf.Bytes()
}

func (s *Server) Close() {
	if s.closed {
		return
	}
	s.closed = true
	close(s.closing)
	s.wg.Wait()
}

func (s *Server) run(listener net.Listener) {
	defer listener.Close()
	defer s.wg.Done()
	for {
		select {
		case <-s.closing:
			return
		default:
			conn, err := listener.Accept()
			if err != nil {
				s.mu.Lock()
				s.errors = append(s.errors, err)
				s.mu.Unlock()
				continue
			}
			s.wg.Add(1)
			go func(conn net.Conn) {
				defer s.wg.Done()
				if err := s.handle(conn); err != nil {
					s.mu.Lock()
					s.errors = append(s.errors, err)
					s.mu.Unlock()
				}
			}(conn)
		}
	}
}

func (s *Server) handle(conn net.Conn) error {
	defer conn.Close()
	buf := make([]byte, 1024)
	n, err := conn.Read(buf)
	if err != nil {
		return err
	}
	msg := buf[:n]
	topic, responses, err := s.readProduceRequest(msg)
	if err != nil {
		return err
	}
	for _, resp := range responses {
		conn.Write(resp)
	}
	s.mu.Lock()
	s.messages = append(s.messages, Message{Topic: topic})
	s.mu.Unlock()
	return nil
}

func (s *Server) readProduceRequest(b []byte) (string, []Response, error) {
	if len(b) < 4 {
		return "", nil, errors.New("invalid message length")
	}
	topicLen := binary.BigEndian.Uint32(b[:4])
	if len(b) < int(topicLen)+4 {
		return "", nil, errors.New("incomplete topic")
	}
	topic := string(b[4 : 4+topicLen])
	msgStart := 4 + topicLen
	if msgStart >= len(b) {
		return "", nil, errors.New("no message data")
	}
	numMsgs := binary.BigEndian.Uint32(b[msgStart : msgStart+4])
	msgStart += 4
	responses := make([]Response, numMsgs)
	for i := 0; i < int(numMsgs); i++ {
		if msgStart+4 > len(b) {
			return "", nil, errors.New("incomplete message count")
		}
		msgLen := binary.BigEndian.Uint32(b[msgStart : msgStart+4])
		msgStart += 4
		if msgStart+int(msgLen) > len(b) {
			return "", nil, errors.New("incomplete message")
		}
		msg := b[msgStart : msgStart+int(msgLen)]
		// Example response
		responses[i] = Response{}
		msgStart += int(msgLen)
	}
	return topic, responses, nil
}

func (s *Server) readStrList(b []byte) ([]string, int) {
	if len(b) < 4 {
		return nil, 0
	}
	count := binary.BigEndian.Uint32(b[:4])
	list := make([]string, count)
	pos := 4
	for i := 0; i < int(count); i++ {
		if pos >= len(b) {
			return nil, pos
		}
		strLen := binary.BigEndian.Uint32(b[pos : pos+4])
		pos += 4
		if pos+int(strLen) > len(b) {
			return nil, pos
		}
		list[i] = string(b[pos : pos+int(strLen)])
		pos += int(strLen)
	}
	return list, pos
}

