type Trojan struct {
	addr      string
	withTLS   bool
	tlsConfig *tls.Config
	certFile  string
	keyFile   string
	pass      [56]byte
	fallback  string
	proxy     proxy.Proxy
}

func (t *Trojan) ListenAndServe() {
	var listener net.Listener
	var err error
	if t.withTLS {
		listener, err = tls.Listen("tcp", t.addr, t.tlsConfig)
	} else {
		listener, err = net.Listen("tcp", t.addr)
	}
	if err != nil {
		panic(err)
	}
	defer listener.Close()

	for {
		conn, err := listener.Accept()
		if err != nil {
			continue
		}
		go t.Serve(conn)
	}
}

func (t *Trojan) Serve(c net.Conn) {
	defer c.Close()

	if t.withTLS {
		tlsConn := tls.Server(c, t.tlsConfig)
		if err := tlsConn.Handshake(); err != nil {
			t.serveFallback(c, t.fallback, nil)
			return
		}
		c = tlsConn
	}

	header := make([]byte, 4)
	if _, err := io.ReadFull(c, header); err != nil {
		t.serveFallback(c, t.fallback, nil)
		return
	}

	version := header[0]
	if version != socks5.Version {
		t.serveFallback(c, t.fallback, nil)
		return
	}

	cmd := socks5.Cmd(header[1])
	if cmd != socks5.CmdConnect && cmd != socks5.CmdUdpAssociate {
		t.serveFallback(c, t.fallback, nil)
		return
	}

	addrType := header[3]
	var target socks5.Addr
	switch addrType {
	case socks5.AddrTypeIPv4:
		var ip [4]byte
		if _, err := io.ReadFull(c, ip[:]); err != nil {
			t.serveFallback(c, t.fallback, nil)
			return
		}
		port := make([]byte, 2)
		if _, err := io.ReadFull(c, port); err != nil {
			t.serveFallback(c, t.fallback, nil)
			return
		}
		target = socks5.Addr{
			Addr: &socks5.AddrIPv4{
				IP:   ip,
				Port: uint16(port[0])<<8 | uint16(port[1]),
			},
		}
	case socks5.AddrTypeDomain:
		var lenByte byte
		if _, err := io.ReadFull(c, []byte{lenByte}); err != nil {
			t.serveFallback(c, t.fallback, nil)
			return
		}
		len := int(lenByte)
		host := make([]byte, len)
		if _, err := io.ReadFull(c, host); err != nil {
			t.serveFallback(c, t.fallback, nil)
			return
		}
		port := make([]byte, 2)
		if _, err := io.ReadFull(c, port); err != nil {
			t.serveFallback(c, t.fallback, nil)
			return
		}
		target = socks5.Addr{
			Addr: &socks5.AddrDomain{
				Domain: string(host),
				Port:   uint16(port[0])<<8 | uint16(port[1]),
			},
		}
	case socks5.AddrTypeIPv6:
		var ip [16]byte
		if _, err := io.ReadFull(c, ip[:]); err != nil {
			t.serveFallback(c, t.fallback, nil)
			return
		}
		port := make([]byte, 2)
		if _, err := io.ReadFull(c, port); err != nil {
			t.serveFallback(c, t.fallback, nil)
			return
		}
		target = socks5.Addr{
			Addr: &socks5.AddrIPv6{
				IP:   ip,
				Port: uint16(port[0])<<8 | uint16(port[1]),
			},
		}
	default:
		t.serveFallback(c, t.fallback, nil)
		return
	}

	if cmd == socks5.CmdUdpAssociate {
		t.ServeUoT(c, target)
		return
	}

	go func() {
		t.proxy.Relay(c, target)
	}()
}

func (t *Trojan) readHeader(r io.Reader) (cmd socks5.Cmd, target socks5.Addr, err error) {
	header := make([]byte, 4)
	if _, err := io.ReadFull(r, header); err != nil {
		return cmd, target, err
	}

	version := header[0]
	if version != socks5.Version {
		return cmd, target, errors.New("unsupported version")
	}

	cmd = socks5.Cmd(header[1])
	if cmd != socks5.CmdConnect && cmd != socks5.CmdBind && cmd != socks5.CmdUdpAssociate {
		return cmd, target, errors.New("unsupported command")
	}

	addrType := header[3]
	var host string
	var port uint16
	switch addrType {
	case socks5.AddrTypeIPv4:
		var ip [4]byte
		if _, err := io.ReadFull(r, ip[:]); err != nil {
			return cmd, target, err
		}
		host = net.IP(ip[:]).String()
		if _, err := io.ReadFull(r, []byte{0, 0}); err != nil {
			return cmd, target, err
		}
		port = uint16(0)
	case socks5.AddrTypeDomain:
		var lenByte byte
		if _, err := io.ReadFull(r, []byte{lenByte}); err != nil {
			return cmd, target, err
		}
		len := int(lenByte)
		hostBytes := make([]byte, len)
		if _, err := io.ReadFull(r, hostBytes); err != nil {
			return cmd, target, err
		}
		host = string(hostBytes)
		if _, err := io.ReadFull(r, []byte{0, 0}); err != nil {
			return cmd, target, err
		}
		port = uint16(0)
	case socks5.AddrTypeIPv6:
		var ip [16]byte
		if _, err := io.ReadFull(r, ip[:]); err != nil {
			return cmd, target, err
		}
		host = net.IP(ip[:]).String()
		if _, err := io.ReadFull(r, []byte{0, 0}); err != nil {
			return cmd, target, err
		}
		port = uint16(0)
	default:
		return cmd, target, errors.New("unsupported address type")
	}

	target = socks5.Addr{
		Addr: &socks5.AddrGeneric{
			Host: host,
			Port: port,
		},
	}
	return cmd, target, nil
}

func (t *Trojan) serveFallback(c net.Conn, fallback string, buf *bytes.Buffer) {
	if fallback == "" {
		c.Close()
		return
	}

	fallbackConn, err := net.Dial("tcp", fallback)
	if err != nil {
		c.Close()
		return
	}

	if buf != nil && buf.Len() > 0 {
		fallbackConn.Write(buf.Bytes())
	}

	var wg sync.WaitGroup
	wg.Add(2)

	go func() {
		defer wg.Done()
		_, err := io.Copy(fallbackConn, c)
		if err != nil {
			fallbackConn.Close()
		}
	}()

	go func() {
		defer wg.Done()
		_, err := io.Copy(c, fallbackConn)
		if err != nil {
			c.Close()
		}
	}()

	wg.Wait()
}

