		if err != nil {
			continue
		}
		go s.Serve(conn)
	}
}

func (s *HTTP) Serve(conn net.Conn) {
	if tcpConn, ok := conn.(*net.TCPConn); ok {
		tcpConn.SetKeepAlive(true)
	}

	reader := bufio.NewReader(conn)
	req, err := http.ReadRequest(reader)
	if err != nil {
		conn.Close()
		return
	}

	if s.user != "" && s.password != "" {
		auth := req.Header.Get("Authorization")
		if auth == "" {
			s.respondUnauthorized(conn)
			conn.Close()
			return
		}
		if !s.checkAuth(auth) {
			s.respondUnauthorized(conn)
			conn.Close()
			return
		}
	}

	if s.pretend {
		s.respond404(conn)
		conn.Close()
		return
	}

	if req.Method == "CONNECT" {
		s.servHTTPS(req, conn)
	} else {
		s.servHTTP(req, &proxy.Conn{})
	}
}

func (s *HTTP) respondUnauthorized(conn net.Conn) {
	conn.Write([]byte("HTTP/1.1 401 Unauthorized\r\n"))
	conn.Write([]byte("WWW-Authenticate: Basic realm=\"proxy\"\r\n"))
	conn.Write([]byte("\r\n"))
}

func (s *HTTP) checkAuth(auth string) bool {
	if !strings.HasPrefix(auth, "Basic ") {
		return false
	}
	decoded, err := base64.StdEncoding.DecodeString(auth[6:])
	if err != nil {
		return false
	}
	parts := strings.Split(string(decoded), ":")
	if len(parts) != 2 {
		return false
	}
	return parts[0] == s.user && parts[1] == s.password
}

func (s *HTTP) respond404(conn net.Conn) {
	conn.Write([]byte("HTTP/1.1 404 Not Found\r\n"))
	conn.Write([]byte("\r\n"))
}

func (s *HTTP) servHTTPS(req *http.Request, conn net.Conn) {
	host, _, err := net.SplitHostPort(req.Host)
	if err != nil {
		s.respond404(conn)
		conn.Close()
		return
	}
	target, err := net.Dial("tcp", req.Host)
	if err != nil {
		s.respond404(conn)
		conn.Close()
		return
	}
	defer target.Close()

	conn.Write([]byte("HTTP/1.1 200 Connection Established\r\n"))
	conn.Write([]byte("Proxy-Agent: MyProxy\r\n"))
	conn.Write([]byte("\r\n"))

	go func() {
		_, _ = io.Copy(target, conn)
	}()
	_, _ = io.Copy(conn, target)
}

func (s *HTTP) servHTTP(req *http.Request, pconn *proxy.Conn) {
	target, err := net.Dial("tcp", req.Host)
	if err != nil {
		s.respond404(pconn.Conn)
		return
	}
	defer target.Close()

	req.Header.Set("Via", "1.1 MyProxy")

	req.Write(target)
	resp, err := http.ReadResponse(bufio.NewReader(target), nil)
	if err != nil {
		s.respond404(pconn.Conn)
		return
	}
	resp.Write(pconn.Conn)
}

