package main

import (
	"context"
	"errors"
	"time"

	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials/insecure"
	"pb"
)

var (
	flagAddress             string
	flagTimeout             int
	flagEnableProfiling     bool
	flagDisableProfiling    bool
	flagRetrieveSnapshot    bool
	flagSnapshot            string
)

func remoteCommand() error {
	var err error
	ctx := context.Background()
	if flagTimeout > 0 {
		ctx, cancel := context.WithTimeout(ctx, time.Duration(flagTimeout)*time.Second)
		defer cancel()
	}

	conn, err := grpc.DialContext(ctx, flagAddress, grpc.WithInsecure(), grpc.WithBlock())
	if err != nil {
		return err
	}
	defer conn.Close()

	client := pb.NewProfilingClient(conn)

	if flagEnableProfiling {
		_, err = client.EnableProfiling(ctx, &pb.Empty{})
		if err != nil {
			return err
		}
	} else if flagDisableProfiling {
		_, err = client.DisableProfiling(ctx, &pb.Empty{})
		if err != nil {
			return err
		}
	} else if flagRetrieveSnapshot {
		req := &pb.SnapshotRequest{Id: flagSnapshot}
		_, err = client.GetSnapshot(ctx, req)
		if err != nil {
			return err
		}
	} else {
		return errors.New("no profiling command specified")
	}

	return nil
}

