package main

import (
	"flag"
	"fmt"
	"log"
	"time"

	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials"
	"google.golang.org/grpc/credentials/tls"
	"google.golang.org/grpc/reflection"
	"google.golang.org/grpc/status"

	route "github.com/grpc/grpc-go/examples/data/route_guide/proto"
)

var (
	tlsFlag             = flag.Bool("tls", false, "enable TLS")
	serverHostOverride  = flag.String("server_host_override", "", "server name override for TLS")
	caFile              = flag.String("ca_file", "", "path to CA cert file")
	serverAddr          = flag.String("server_addr", "localhost:50051", "server address")
)

func main() {
	flag.Parse()

	var opts []grpc.DialOption

	if *tlsFlag {
		creds, err := credentials.NewTLS(&tls.Config{
			ServerName: *serverHostOverride,
		})
		if err != nil {
			log.Fatalf("Failed to create TLS credentials: %v", err)
		}
		opts = append(opts, grpc.WithTransportCredentials(creds))
	} else {
		opts = append(opts, grpc.WithInsecure())
	}

	conn, err := grpc.Dial(*serverAddr, opts...)
	if err != nil {
		log.Fatalf("Failed to connect: %v", err)
	}
	defer conn.Close()

	client := route.NewRouteGuideClient(conn)
	reflection.Register(conn)

	printFeature(client, &route.Point{Latitude: 409437364, Longitude: -740778902})
	printFeatures(client, &route.Rectangle{
		Lo: &route.Point{Latitude: 409437364, Longitude: -740778902},
		Hi: &route.Point{Latitude: 409437364, Longitude: -740778902},
	})
	runRecordRoute(client)
	runRouteChat(client)
}

func printFeature(client route.RouteGuideClient, point *route.Point) {
	ctx, cancel := context.WithTimeout(context.Background(), time.Second)
	defer cancel()

	resp, err := client.GetFeature(ctx, point)
	if err != nil {
		log.Printf("Failed to get feature: %v", err)
		return
	}
	fmt.Printf("Feature found: %s at (%d, %d)\n", resp.Name, resp.Location.Latitude, resp.Location.Longitude)
}

func printFeatures(client route.RouteGuideClient, rect *route.Rectangle) {
	ctx, cancel := context.WithTimeout(context.Background(), time.Second)
	defer cancel()

	stream, err := client.ListFeatures(ctx, rect)
	if err != nil {
		log.Printf("Failed to list features: %v", err)
		return
	}

	for {
		resp, err := stream.Recv()
		if err != nil {
			if status.Code(err) == status.Code(err) == status.Canceled {
				return
			}
			log.Printf("Error receiving feature: %v", err)
			return
		}
		fmt.Printf("Feature: %s at (%d, %d)\n", resp.Name, resp.Location.Latitude, resp.Location.Longitude)
	}
}

func runRecordRoute(client route.RouteGuideClient) {
	ctx, cancel := context.WithTimeout(context.Background(), time.Second)
	defer cancel()

	stream, err := client.RecordRoute(ctx)
	if err != nil {
		log.Fatalf("Failed to create record route stream: %v", err)
	}

	points := []*route.Point{
		{Latitude: 409437364, Longitude: -740778902},
		{Latitude: 409437364, Longitude: -740778902},
	}

	for _, p := range points {
		if err := stream.Send(p); err != nil {
			log.Fatalf("Failed to send point: %v", err)
		}
	}

	resp, err := stream.CloseAndRecv()
	if err != nil {
		log.Fatalf("Failed to receive record route response: %v", err)
	}
	fmt.Printf("Route summary: %d points, %d distance, %d time, %d features\n",
		resp.PointCount, resp.Distance, resp.ElapsedTime, resp.FeatureCount)
}

func runRouteChat(client route.RouteGuideClient) {
	ctx, cancel := context.WithTimeout(context.Background(), time.Second)
	defer cancel()

	stream, err := client.RouteChat(ctx)
	if err != nil {
		log.Fatalf("Failed to create route chat stream: %v", err)
	}

	go func() {
		for {
			msg, err := stream.Recv()
			if err != nil {
				if status.Code(err) == status.Canceled {
					return
				}
				log.Printf("Error receiving chat message: %v", err)
				return
			}
			fmt.Printf("Received: %s at (%d, %d)\n", msg.Message, msg.Location.Latitude, msg.Location.Longitude)
		}
	}()

	for i := 0; i < 3; i++ {
		msg := &route.RouteNote{
			Location: &route.Point{Latitude: 409437364, Longitude: -740778902},
			Message:  fmt.Sprintf("Hello %d", i),
		}
		if err := stream.Send(msg); err != nil {
			log.Fatalf("Failed to send chat message: %v", err)
		}
		time.Sleep(100 * time.Millisecond)
	}
}

