	lastSuccess time.Time
	lastError   error
	lastLatency time.Duration
}

func (p *Probe) connectWithRetry(ctx context.Context, tgt, msgPattern string, result *probeRunResult) *grpc.ClientConn {
	var conn *grpc.ClientConn
	var err error
	maxRetries := 5
	retryDelay := 5 * time.Second

	for i := 0; i < maxRetries; i++ {
		conn, err = grpc.DialContext(ctx, tgt, p.dialOpts...)
		if err == nil {
			return conn
		}
		p.l.Errorf("Failed to connect to %s: %v, retrying in %v", tgt, err, retryDelay)
		select {
		case <-ctx.Done():
			return nil
		case <-time.After(retryDelay):
		}
	}
	p.l.Errorf("Failed to connect to %s after %d retries: %v", tgt, maxRetries, err)
	result.lastError = err
	return nil
}

func (p *Probe) oneTargetLoop(ctx context.Context, tgt string, index int, result *probeRunResult) {
	ticker := time.NewTicker(p.opts.ProbeInterval)
	defer ticker.Stop()

	for {
		select {
		case <-ctx.Done():
			return
		case <-ticker.C:
			start := time.Now()
			conn := p.connectWithRetry(ctx, tgt, "", result)
			if conn == nil {
				result.lastError = fmt.Errorf("connection failed")
				result.lastLatency = time.Since(start)
				continue
			}
