func (s *struct) start(ctx context.Context) error {
	conn, err := grpc.DialContext(ctx, s.cfg.Address, grpc.WithBlock(), grpc.WithInsecure())
	if err != nil {
		s.log.Error("failed to dial gRPC server", zap.Error(err))
		return err
	}
	defer conn.Close()

	client := cloudslack.NewCloudSlackClient(conn)

	stream, err := client.StreamEvents(ctx)
	if err != nil {
		s.log.Error("failed to create stream", zap.Error(err))
		return err
	}
	defer stream.CloseSend()

	req := &cloudslack.ConnectionRequest{
		BotId:      s.botID,
		ClusterName: s.clusterName,
		Metadata:    s.commGroupMetadata,
	}
	if err := stream.Send(req); err != nil {
		s.log.Error("failed to send connection request", zap.Error(err))
		return err
	}

	for {
		select {
		case <-ctx.Done():
			return nil
		default:
			resp, err := stream.Recv()
			if err != nil {
				if status.Code(err) == codes.Canceled {
					return nil
				}
				if err == io.EOF {
					s.log.Info("stream closed by server")
					return nil
				}
				s.log.Error("error receiving event", zap.Error(err))
				return err
			}
			s.handleEvent(resp)
		}
	}
}

