	listeners map[string][]*listenerStream
}

type listenerStream struct {
	stream pb.Plugin_RegisterListenerServer
	pattern string
	oneTime bool
}

func (s *pluginServer) RegisterListener(stream pb.Plugin_RegisterListenerServer) error {
	for {
		msg, err := stream.Recv()
		if err != nil {
			if status.Code(err) == status.Code(0) {
				s.lock.Lock()
				for _, l := range s.listeners[msg.Pattern] {
					if l.stream == stream {
						s.removeListener(l)
						break
					}
				}
				s.lock.Unlock()
			}
			return err
		}

		if msg.Pattern != "" {
			s.lock.Lock()
			s.listeners[msg.Pattern] = append(s.listeners[msg.Pattern], &listenerStream{
				stream:  stream,
				pattern: msg.Pattern,
				oneTime: msg.OneTime,
			})
			s.lock.Unlock()
		}

		if msg.Data != nil {
			resp := &pb.Response{Data: msg.Data}
			if err := stream.Send(resp); err != nil {
				return err
			}
		}
	}
}

func (s *pluginServer) removeListener(l *listenerStream) {
	for i, listener := range s.listeners[l.pattern] {
		if listener == l {
			s.listeners[l.pattern] = append(s.listeners[l.pattern][:i], s.listeners[l.pattern][i+1:]...)
			if len(s.listeners[l.pattern]) == 0 {
				delete(s.listeners, l.pattern)
			}
			break
		}
	}
}

