	msg, err := stream.Recv()
	if err != nil {
		return err
	}

	require.Equal(cs.t, pb.ChaincodeMessage_REGISTER.String(), msg.Type.String())
	chaincodeID := &pb.ChaincodeID{}
	err = proto.Unmarshal(msg.Payload, chaincodeID)
	if err != nil {
		return err
	}
	require.Equal(cs.t, cs.expectedCCname, chaincodeID.Name)
