	"google.golang.org/grpc/keepalive"
	"google.golang.org/grpc/metadata"
	"google.golang.org/grpc/peer"
	"golang.org/x/net/http2"
)

type Server struct {
	grpcServer *grpc.Server
	bufferSize int
}

func NewServer() *Server {
	return &Server{
		bufferSize: 1024 * 1024 * 4,
	}
}

func (s *Server) Start(url *common.URL) {
	addr := fmt.Sprintf("%s:%d", url.Host, url.Port)
	var listener net.Listener
	var err error

	if url.UseTLS {
		tlsConfig := &tls.Config{
			Certificates: []tls.Certificate{{
				Certificate: [][]byte{url.Cert},
				PrivateKey:  url.Key,
			}},
		}
		listener, err = tls.Listen("tcp", addr, tlsConfig)
	} else {
		listener, err = net.Listen("tcp", addr)
	}
	if err != nil {
		panic(err)
	}

	opts := []grpc.ServerOption{
		grpc.MaxRecvMsgSize(s.bufferSize),
		grpc.MaxSendMsgSize(s.bufferSize),
		grpc.KeepaliveParams(keepalive.ServerParameters{
			MaxConnectionIdle:     15 * time.Minute,
			MaxConnectionAge:      5 * time.Minute,
			MaxConnectionAgeGrace: 10 * time.Second,
			Time:                  30 * time.Second,
			Timeout:               20 * time.Second,
		}),
		grpc.KeepaliveEnforcementPolicy(keepalive.EnforcementPolicy{
			MinTime:             5 * time.Second,
			PermitWithoutStream: true,
		}),
		grpc.UnaryInterceptor(func(ctx context.Context, req interface{}, info *grpc.UnaryServerInfo, handler grpc.UnaryHandler) (interface{}, error) {
			span, _ := opentracing.StartSpanFromContext(ctx, info.FullMethod)
			defer span.Finish()
			return handler(ctx, req)
		}),
		grpc.StreamInterceptor(func(srv interface{}, stream grpc.ServerStream, info *grpc.StreamServerInfo, handler grpc.StreamHandler) error {
			span, _ := opentracing.StartSpanFromContext(stream.Context(), info.FullMethod)
			defer span.Finish()
			return handler(srv, stream)
		}),
	}

	s.grpcServer = grpc.NewServer(opts...)
	reflection.Register(s.grpcServer)

	go func() {
		if err := s.grpcServer.Serve(listener); err != nil {
			panic(err)
		}
	}()
}

func (s *Server) Stop() {
	s.grpcServer.Stop()
}

func (s *Server) GracefulStop() {
	s.grpcServer.GracefulStop()
}

