	onlineUsers map[string]bool
}

func (s *server) QueryUsersOnline(ctx context.Context, req *protobuf.QueryUsersOnlineReq) (*protobuf.QueryUsersOnlineRsp, error) {
	s.mu.Lock()
	defer s.mu.Unlock()
	online := s.onlineUsers[req.Username]
	return &protobuf.QueryUsersOnlineRsp{Online: online}, nil
}

func (s *server) SendMsg(ctx context.Context, req *protobuf.SendMsgReq) (*protobuf.SendMsgRsp, error) {
	s.mu.Lock()
	defer s.mu.Unlock()
	if !s.onlineUsers[req.Username] {
		return &protobuf.SendMsgRsp{Success: false}, fmt.Errorf("user not online")
	}
	return &protobuf.SendMsgRsp{Success: true}, nil
}

func (s *server) SendMsgAll(ctx context.Context, req *protobuf.SendMsgAllReq) (*protobuf.SendMsgAllRsp, error) {
	s.mu.Lock()
	defer s.mu.Unlock()
	return &protobuf.SendMsgAllRsp{Success: true}, nil
}

