package grpcserver

import (
	"context"
	"crypto/tls"
	"net"
	"time"

	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials"
	"google.golang.org/grpc/keepalive"
	"github.com/rs/zerolog"
	"github.com/yourorg/certwatcher"
)

type GRPCServerConfig struct {
	Address        string
	Network        string
	TLSCertPath    string
	TLSKeyPath     string
	MaxConnAge     time.Duration
	Enabled        bool
	BufferSize     int
	ClientCAPath   string
	MaxWorkers     uint32
	flagPrefix     string
}

type RunnableGRPCServer interface {
	Listen(ctx context.Context) func() error
	DialContext(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error)
	NetDialContext(ctx context.Context, network, address string) (net.Conn, error)
	Insecure() bool
	GracefulStop()
	WithOpts(...grpc.ServerOption) RunnableGRPCServer
}

type completedGRPCServer struct {
	opts           []grpc.ServerOption
	listener       net.Listener
	svcRegistrationFn func(*grpc.Server)
	listenFunc     func() error
	prestopFunc    func()
	stopFunc       func()
	dial           func(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error)
	netDial        func(context.Context, string) (net.Conn, error)
	creds          credentials.TransportCredentials
	certWatcher    *certwatcher.CertWatcher
}

func (c *GRPCServerConfig) Complete(level zerolog.Level, svcRegistrationFn func(*grpc.Server), opts ...grpc.ServerOption) (RunnableGRPCServer, error) {
	if !c.Enabled {
		return nil, nil
	}

	listener, dial, netDial, err := c.listenerAndDialer()
	if err != nil {
		return nil, err
	}

	tlsOpts, certWatcher, err := c.tlsOpts()
	if err != nil {
		return nil, err
	}

	allOpts := append(tlsOpts, opts...)
	server := grpc.NewServer(allOpts...)
	svcRegistrationFn(server)

	return &completedGRPCServer{
		opts:           allOpts,
		listener:       listener,
		svcRegistrationFn: svcRegistrationFn,
		listenFunc: func() error {
			return server.Serve(listener)
		},
		stopFunc: func() {
			server.GracefulStop()
		},
		dial: dial,
		netDial: netDial,
		creds:  c.clientCreds(),
		certWatcher: certWatcher,
	}, nil
}

func (c *GRPCServerConfig) listenerAndDialer() (net.Listener, func(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error), func(context.Context, string) (net.Conn, error), error) {
	var listener net.Listener
	var err error

	switch c.Network {
	case "tcp", "unix":
		listener, err = net.Listen(c.Network, c.Address)
		if err != nil {
			return nil, nil, nil, err
		}
	default:
		return nil, nil, nil, nil
	}

	dial := func(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
		return grpc.DialContext(ctx, c.Address, append(opts, grpc.WithTransportCredentials(c.creds))...)
	}

	netDial := func(ctx context.Context, addr string) (net.Conn, error) {
		return net.DialContext(ctx, c.Network, addr)
	}

	return listener, dial, netDial, nil
}

func (c *GRPCServerConfig) tlsOpts() ([]grpc.ServerOption, *certwatcher.CertWatcher, error) {
	var serverOpts []grpc.ServerOption
	var certWatcher *certwatcher.CertWatcher

	if c.TLSCertPath != "" && c.TLSKeyPath != "" {
		cert, err := tls.LoadX509KeyPair(c.TLSCertPath, c.TLSKeyPath)
		if err != nil {
			return nil, nil, err
		}

		config := &tls.Config{
			Certificates: []tls.Certificate{cert},
		}

		if c.ClientCAPath != "" {
			caCert, err := tls.LoadX509KeyPair(c.ClientCAPath, "")
			if err != nil {
				return nil, nil, err
			}
			config.ClientCAs = caCert.Certificate
			config.ClientAuth = tls.RequireAndVerifyClientCert
		}

		certWatcher = certwatcher.New(c.TLSCertPath, c.TLSKeyPath)
		certWatcher.Start()

		serverOpts = append(serverOpts, grpc.Creds(credentials.NewTLS(config)))
		serverOpts = append(serverOpts, grpc.KeepaliveParams(keepalive.ServerParameters{
			MaxConnectionAge: c.MaxConnAge,
		}))
		serverOpts = append(serverOpts, grpc.MaxConcurrentStreams(c.MaxWorkers))
	}

	return serverOpts, certWatcher, nil
}

func (c *GRPCServerConfig) clientCreds() credentials.TransportCredentials {
	if c.TLSCertPath != "" && c.TLSKeyPath != "" {
		creds, _ := credentials.NewClientTLSFromFile(c.TLSCertPath, "")
		return creds
	}
	return credentials.Insecure()
}

func (s *completedGRPCServer) Listen(ctx context.Context) func() error {
	return s.listenFunc
}

func (s *completedGRPCServer) DialContext(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
	return s.dial(ctx, opts...)
}

func (s *completedGRPCServer) NetDialContext(ctx context.Context, network, address string) (net.Conn, error) {
	return s.netDial(ctx, address)
}

func (s *completedGRPCServer) Insecure() bool {
	return s.creds == credentials.Insecure()
}

func (s *completedGRPCServer) GracefulStop() {
	s.stopFunc()
}

func (s *completedGRPCServer) WithOpts(opts ...grpc.ServerOption) RunnableGRPCServer {
	newOpts := append(s.opts, opts...)
	return &completedGRPCServer{
		opts:           newOpts,
		listener:       s.listener,
		svcRegistrationFn: s.svcRegistrationFn,
		listenFunc:     s.listenFunc,
		prestopFunc:    s.prestopFunc,
		stopFunc:       s.stopFunc,
		dial:           s.dial,
		netDial:        s.netDial,
		creds:          s.creds,
		certWatcher:    s.certWatcher,
	}
}

