package main

import (
	"context"
	"errors"
	"fmt"
	"log"
	"net"
	"sync"
	"time"

	"google.golang.org/grpc"
	"google.golang.org/grpc/codes"
	"google.golang.org/grpc/status"
	"google.golang.org/protobuf/proto"
	"google.golang.org/protobuf/types/known/timestamppb"

	"github.com/example/logservice/api"
	"github.com/example/logservice/pb"
	"github.com/example/logservice/event"
	"github.com/example/logservice/batch"
)

type Source struct {
	name       string
	eventPool  *event.Pool
	config     *Config
	grpcServer *grpc.Server
	bc         *batchChain
	mu         sync.Mutex
	running    bool
}

type Config struct {
	BindAddress string
	Port        int
}

type batchChain struct {
	batchSize int
	batchWait time.Duration
	next      func([]*event.Event) error
}

func NewBatchChain(batchSize int, batchWait time.Duration, next func([]*event.Event) error) *batchChain {
	return &batchChain{
		batchSize: batchSize,
		batchWait: batchWait,
		next:      next,
	}
}

func (bc *batchChain) Process(events []*event.Event) error {
	if len(events) < bc.batchSize {
		return nil
	}
	return bc.next(events)
}

func NewSource(name string, config *Config, eventPool *event.Pool, bc *batchChain) *Source {
	return &Source{
		name:       name,
		eventPool:  eventPool,
		config:     config,
		bc:         bc,
	}
}

func (s *Source) ProductLoop(productFunc api.ProductFunc) {
	s.mu.Lock()
	defer s.mu.Unlock()
	if s.running {
		return
	}
	s.running = true

	addr := fmt.Sprintf("%s:%d", s.config.BindAddress, s.config.Port)
	lis, err := net.Listen("tcp", addr)
	if err != nil {
		log.Fatalf("failed to listen: %v", err)
	}
	s.grpcServer = grpc.NewServer()
	pb.RegisterLogServiceServer(s.grpcServer, s)
	go func() {
		if err := s.grpcServer.Serve(lis); err != nil {
			log.Fatalf("failed to serve: %v", err)
		}
	}()
	log.Printf("Server is running on %s", addr)

	for {
		select {}
	}
}

func (s *Source) LogStream(stream pb.LogService_LogStreamServer) error {
	ctx := stream.Context()
	logEvents := make([]*event.Event, 0, s.bc.batchSize)
	ticker := time.NewTicker(s.bc.batchWait)
	defer ticker.Stop()

	for {
		select {
		case <-ctx.Done():
			return ctx.Err()
		case <-ticker.C:
			if len(logEvents) > 0 {
				if err := s.bc.Process(logEvents); err != nil {
					return err
				}
				logEvents = logEvents[:0]
			}
		default:
			msg, err := stream.Recv()
			if err != nil {
				if errors.Is(err, io.EOF) {
					if len(logEvents) > 0 {
						if err := s.bc.Process(logEvents); err != nil {
							return err
						}
					}
					return nil
				}
				return status.Errorf(codes.Unknown, "failed to receive message: %v", err)
			}
			event := &event.Event{
				ID:      msg.Id,
				Message: msg.Message,
				Timestamp: timestamppb.New(time.Now()),
				Metadata: make(map[string]string),
			}
			for k, v := range msg.Metadata {
				event.Metadata[k] = v
			}
			logEvents = append(logEvents, event)
			if len(logEvents) >= s.bc.batchSize {
				if err := s.bc.Process(logEvents); err != nil {
					return err
				}
				logEvents = logEvents[:0]
			}
		}
