func Start(addr string, enableTLS bool, certificate auth.Certificate) {
	var lis net.Listener
	var err error

	if enableTLS {
		cert, err := tls.LoadX509KeyPair(certificate.CertFile, certificate.KeyFile)
		if err != nil {
			log.Fatalf("failed to load certificate: %v", err)
		}
		config := &tls.Config{Certificates: []tls.Certificate{cert}}
		lis, err = tls.Listen("tcp", addr, config)
	} else {
		lis, err = net.Listen("tcp", addr)
	}
	if err != nil {
		log.Fatalf("failed to listen: %v", err)
	}

	keepaliveOptions := []grpc.ServerOption{
		grpc.KeepaliveParams(keepalive.ServerParameters{
			MaxConnectionIdle:     15 * time.Second,
			MaxConnectionAge:      15 * time.Second,
			MaxConnectionAgeGrace: 5 * time.Second,
			Time:                  5 * time.Second,
			Timeout:               5 * time.Second,
		}),
		grpc.KeepaliveEnforcementPolicy(keepalive.EnforcementPolicy{
			MinTime:             5 * time.Second,
			PermitWithoutStream: true,
		}),
	}

