	GRPCServer      *grpc.Server
	Listener        string
	Endpoint        string
	ServerTLSConfig tls.Config
}

func createListener(config Config) (net.Listener, error) {
	parts := strings.SplitN(config.Listener, "://", 2)
	if len(parts) != 2 {
		return nil, fmt.Errorf("invalid listener address format: %s", config.Listener)
	}
	network := parts[0]
	addr := parts[1]

	var listener net.Listener
	var err error

	switch network {
	case "unix":
		listener, err = net.Listen(network, addr)
	case "tcp":
		listener, err = net.Listen(network, addr)
	default:
		return nil, fmt.Errorf("unsupported network type: %s", network)
	}

	if err != nil {
		return nil, err
	}

	return listener, nil
}

func grpcServer(config Config) (*grpc.Server, error) {
	var opts []grpc.ServerOption

	if config.ServerTLSConfig != nil {
		opts = append(opts, grpc.Creds(credentials.NewTLS(&config.ServerTLSConfig)))
	}

	keepaliveParams := grpc.KeepaliveParams(keepalive.ServerParameters{
		MaxConnectionIdle:     15 * time.Second,
		MaxConnectionAge:      15 * time.Second,
		MaxConnectionAgeGrace: 5 * time.Second,
		Time:                  5 * time.Second,
		Timeout:               20 * time.Second,
	})
	opts = append(opts, keepaliveParams)

	enforcementPolicy := grpc.KeepaliveEnforcementPolicy(keepalive.EnforcementPolicy{
		MinTime:             5 * time.Second,
		PermitWithoutStream: true,
	})
	opts = append(opts, enforcementPolicy)

	server := grpc.NewServer(opts...)
	return server, nil
}

