		notifyChanClose:   make(chan *amqp.Error),
		notifyConfirm:     make(chan amqp.Confirmation),
		isReady:           false,
	}
	s.connect(addr)
	go s.handleReconnect(addr)
	return s
}

func (s *Session) handleReconnect(addr string) {
	for {
		select {
		case err := <-s.notifyConnClose:
			s.logger.Printf("Connection closed: %v", err)
			s.connection = nil
			s.channel = nil
			s.isReady = false
			if err := s.connect(addr); err != nil {
