			if ok := s.handleReInit(s.connection); !ok {
				s.logger.Println("Failed to reinitialize channel after reconnect")
				s.connection.Close()
				s.connection = nil
				s.channel = nil
				s.isReady = false
			}
		case <-s.done:
			return
		}
