type Listener struct {
	URL        *url.URL
	Token      string
	SchemaFunc func([]byte) (io.Reader, error)
	Out        io.Writer
	InCh       <-chan []byte
}

func NewListener(url *url.URL, token string, schemaFunc func([]byte) (io.Reader, error), out io.Writer, inCh <-chan []byte) *Listener {
	newURL := *url
	q := newURL.Query()
	q.Add("token", token)
	newURL.RawQuery = q.Encode()
	return &Listener{
		URL:        &newURL,
		Token:      token,
		SchemaFunc: schemaFunc,
		Out:        out,
		InCh:       inCh,
	}
}

func (l *Listener) Listen(ctx context.Context) error {
	conn, _, err := websocket.DefaultDialer.Dial(l.URL.String(), nil)
	if err != nil {
		return err
	}
	defer conn.Close()

	go func() {
		for {
			select {
			case <-ctx.Done():
				return
			default:
				_, message, err := conn.ReadMessage()
				if err != nil {
					if websocket.IsCloseError(err, websocket.CloseNormalClosure) {
						return
					}
					log.Println("Read error:", err)
					return
				}
				if l.SchemaFunc != nil {
					reader, err := l.SchemaFunc(message)
					if err != nil {
						log.Println("SchemaFunc error:", err)
						return
					}
					_, err = io.Copy(l.Out, reader)
					if err != nil {
						log.Println("Write to Out error:", err)
						return
					}
				} else {
					_, err := l.Out.Write(message)
					if err != nil {
						log.Println("Write to Out error:", err)
						return
					}
				}
			}
		}
	}()

	go func() {
		for msg := range l.InCh {
			select {
			case <-ctx.Done():
				return
			default:
				err := conn.WriteMessage(websocket.TextMessage, msg)
				if err != nil {
					log.Println("Write error:", err)
					return
				}
			}
		}
	}()

	<-ctx.Done()
	return nil
}

