	if err != nil {
		http.Error(w, "Could not open WebSocket connection", http.StatusServiceUnavailable)
		return
	}
	defer conn.Close()

	guacConn, err := connectToGuacamole(s.Host, s.Port, s.Username, s.Password, s.ConnectionId, s.Width, s.Height, s.Dpi)
	if err != nil {
		http.Error(w, "Failed to connect to Guacamole", http.StatusInternalServerError)
		return
	}
	defer guacConn.Close()

	go func() {
		for {
			_, msg, err := conn.ReadMessage()
			if err != nil {
				break
			}
			err = guacConn.WriteMessage(websocket.TextMessage, msg)
			if err != nil {
				break
			}
		}
		conn.Close()
		guacConn.Close()
	}()

	go func() {
		for {
			_, msg, err := guacConn.ReadMessage()
			if err != nil {
				break
			}
			err = conn.WriteMessage(websocket.TextMessage, msg)
			if err != nil {
				break
			}
		}
		conn.Close()
		guacConn.Close()
	}()
}

func connectToGuacamole(host string, port int, username, password, connectionId string, width, height, dpi int) (*websocket.Conn, error) {
	url := "ws://" + host + ":" + string(port) + "/guacamole/websocket"
	header := http.Header{}
	header.Set("Authorization", "Basic "+encodeCredentials(username, password))
	header.Set("ConnectionId", connectionId)
	header.Set("Width", string(width))
	header.Set("Height", string(height))
	header.Set("Dpi", string(dpi))

	return websocket.DefaultDialer.Dial(url, header)
}

func encodeCredentials(username, password string) string {
	return base64.StdEncoding.EncodeToString([]byte(username + ":" + password))
}

