package main

import (
	"io"
	"log"
	"net/http"
	"time"

	"github.com/gin-gonic/gin"
	"github.com/gorilla/websocket"
	"golang.org/x/crypto/ssh"
)

var upgrader = websocket.Upgrader{
	CheckOrigin: func(r *http.Request) bool {
		return true
	},
}

func ConnectSshPmExec(c *gin.Context, username, envName, productName, ip, hostId string, cols, rows int, log *log.Logger) error {
	ws, err := upgrader.Upgrade(c.Writer, c.Request, nil)
	if err != nil {
		log.Printf("Failed to upgrade to WebSocket: %v", err)
		return err
	}
	defer ws.Close()

	privateKey, err := getPrivateKey(hostId)
	if err != nil {
		log.Printf("Failed to retrieve private key: %v", err)
		return err
	}

	signer, err := ssh.ParsePrivateKey(privateKey)
	if err != nil {
		log.Printf("Failed to parse private key: %v", err)
		return err
	}

	config := &ssh.ClientConfig{
		User: username,
		Auth: []ssh.AuthMethod{
			ssh.PublicKeys(signer),
		},
		HostKeyCallback: ssh.InsecureIgnoreHostKey(),
	}

	client, err := ssh.Dial("tcp", ip+":22", config)
	if err != nil {
		log.Printf("Failed to dial SSH client: %v", err)
		return err
	}
	defer client.Close()

	session, err := client.NewSession()
	if err != nil {
		log.Printf("Failed to create SSH session: %v", err)
		return err
	}
	defer session.Close()

	modes := ssh.TerminalModes{
		ssh.ECHO:          1,
		ssh.TTY_OP_ISPEED: 14400,
		ssh.TTY_OP_OSPEED: 14400,
	}
	if err := session.RequestPty("xterm", rows, cols, modes); err != nil {
		log.Printf("Failed to request PTY: %v", err)
		return err
	}

	if err := session.Start("/bin/bash -i"); err != nil {
		log.Printf("Failed to start shell: %v", err)
		return err
	}

	go func() {
		reader, err := session.StdoutPipe()
		if err != nil {
			log.Printf("Failed to get stdout pipe: %v", err)
			return
		}
		defer reader.Close()
		buf := make([]byte, 1024)
		for {
			n, err := reader.Read(buf)
			if err != nil {
				if err == io.EOF {
