package main

import (
	"bytes"
	"context"
	"encoding/binary"
	"errors"
	"fmt"
	"math/rand"
	"net"
	"sync"
	"sync/atomic"
	"time"
)

type Bootstrap struct {
	fqdn        string
	port        uint16
	bootstrap   *net.UDPAddr
	qt          uint16
	logger      *log.Logger
	updating    atomic.Bool
	nextUpdate  time.Time
	readyNotify chan struct{}
	m           sync.Mutex
	ready       bool
	addrStr     string
}

func (b *Bootstrap) resolve(ctx context.Context, qt uint16) (netip.Addr, uint32, error) {
	b.m.Lock()
	if b.ready {
		b.m.Unlock()
		return netip.MustParseAddr(b.addrStr), 0, nil
	}
	if b.updating.Load() {
		b.m.Unlock()
		select {
		case <-ctx.Done():
			return netip.Addr{}, 0, ctx.Err()
		case <-b.readyNotify:
			b.m.Lock()
			defer b.m.Unlock()
			return netip.MustParseAddr(b.addrStr), 0, nil
		}
	}
	b.updating.Store(true)
	b.m.Unlock()

	var addr netip.Addr
	var ttl uint32
	var err error

	for attempt := 0; attempt < 3; attempt++ {
		conn, err := net.DialUDP("udp", nil, b.bootstrap)
		if err != nil {
			b.logger.Printf("failed to dial UDP: %v", err)
			continue
		}
		defer conn.Close()

		msg := buildDNSQuery(b.fqdn, qt)
		if _, err := conn.Write(msg); err != nil {
			b.logger.Printf("failed to write DNS query: %v", err)
			continue
		}

		buffer := make([]byte, 512)
		n, _, err := conn.ReadFromUDP(buffer, nil)
		if err != nil {
			b.logger.Printf("failed to read DNS response: %v", err)
			continue
		}

		parsedAddr, parsedTTL, e := parseDNSResponse(buffer[:n], qt)
		if e != nil {
			b.logger.Printf("failed to parse DNS response: %v", e)
			continue
		}

		addr = parsedAddr
		ttl = parsedTTL
		err = nil
		break
	}

	if err != nil {
		return netip.Addr{}, 0, err
	}

	b.m.Lock()
	b.addrStr = addr.String()
	b.ready = true
	b.updating.Store(false)
	close(b.readyNotify)
	b.m.Unlock()

	return addr, ttl, nil
}

func buildDNSQuery(fqdn string, qt uint16) []byte {
	msg := make([]byte, 12)
	id := uint16(rand.Intn(65535))
	binary.BigEndian.PutUint16(msg[0:], id)
	binary.BigEndian.PutUint16(msg[2:], 0x0100)
	binary.BigEndian.PutUint16(msg[4:], 1)
	binary.BigEndian.PutUint16(msg[6:], 0)
	binary.BigEndian.PutUint16(msg[8:], 0)
	binary.BigEndian.PutUint16(msg[10:], 0)

	question := encodeDomainName(fqdn)
	msg = append(msg, question...)
	msg = append(msg, byte(qt>>8), byte(qt&0xFF), 0x00, 0x01)

	return msg
}

func encodeDomainName(s string) []byte {
	var result []byte
	for _, part := range strings.Split(s, ".") {
		result = append(result, byte(len(part)))
		result = append(result, []byte(part)...)
	}
	result = append(result, 0x00)
	return result
}

func parseDNSResponse(data []byte, qt uint16) (netip.Addr, uint32, error) {
	if len(data) < 12 {
		return netip.Addr{}, 0, errors.New("response too short")
	}
	flags := binary.BigEndian.Uint16(data[2:])
	if flags&0x000F != 0 {
		return netip.Addr{}, 0, fmt.Errorf("DNS error: %d", flags&0x000F)
	}
	questions := binary.BigEndian.Uint16(data[4:])
	if questions != 1 {
		return netip.Addr{}, 0, errors.New("unexpected number of questions")
	}
	answerRRs := binary.BigEndian.Uint16(data[6:])
	if answerRRs == 0 {
		return netip.Addr{}, 0, errors.New("no answers in response")
	}

	offset := 12
	domainName, offset := parseDomainName(data, offset)
	if domainName != fqdn {
		return netip.Addr{}, 0, errors.New("domain name mismatch")
	}
	qtype := binary.BigEndian.Uint16(data[offset:])
	offset += 2
	qclass := binary.BigEndian.Uint16(data[offset:])
	offset += 2

	for i := 0; i < int(answerRRs); i++ {
		name, nameLen := parseDomainName(data, offset)
		offset += nameLen
		rtype := binary.BigEndian.Uint16(data[offset:])
		offset += 2
		rclass := binary.BigEndian.Uint16(data[offset:])
		offset += 2
		ttl := binary.BigEndian.Uint32(data[offset:])
		offset += 4
		rdlength := binary.BigEndian.Uint16(data[offset:])
		offset += 2
		rdata := data[offset : offset+int(rdlength)]
		offset += int(rdlength)

		if rtype == qt {
			if rtype == 1 {
				if len(rdata) != 4 {
					continue
				}
				ip := net.IP(rdata).To4()
				if ip == nil {
					continue
				}
				return netip.AddrFromIPv4(ip), ttl, nil
			} else if rtype == 28 {
				if len(rdata) != 16 {
					continue
				}
				ip := net.IP(rdata).To16()
				if ip == nil {
					continue
				}
				return netip.AddrFromIPv6(ip), ttl, nil
			}
		}
	}

	return netip.Addr{}, 0, errors.New("no matching answer found")
}

func parseDomainName(data []byte, offset int) (string, int) {
	var result strings.Builder
	for {
		if offset >= len(data) {
			return "", offset
		}
		length := int(data[offset])
		offset++
		if length == 0 {
			break
		}
		if offset+length > len(data) {
			return "", offset
		}
		result.WriteString(string(data[offset : offset+length]))
		offset += length
		if offset < len(data) && data[offset] != 0 {
			result.WriteString(".")
		}
		offset++
	}
	return result.String(), offset
}

