	Mutex                  sync.Mutex
	Params                 *gosnmp.GoSNMP
	OnNewTrap              func(*gosnmp.SnmpPacket, *net.UDPAddr)
	CloseTimeout           time.Duration
	conn                   *net.UDPConn
