	usmStatsUnknownEngineIDsCount uint32
	finish                 int32
}

type TrapHandlerFunc func(*gosnmp.SnmpPacket, *net.UDPAddr)

func NewTrapListener() *TrapListener {
	return &TrapListener{
		done:                   make(chan bool),
		listening:              make(chan bool),
		Params:                 &gosnmp.GoSNMP{
			Transport:     "udp",
			Version:       gosnmp.Version3,
			SecurityLevel: gosnmp.NoAuthNoPriv,
			SecurityName:  "public",
		},
		CloseTimeout: 5 * time.Second,
		proto:        "udp",
	}
}

func (t *TrapListener) Close() {
	if atomic.CompareAndSwapInt32(&t.finish, 0, 1) {
		close(t.done)
		select {
		case <-t.listening:
		case <-time.After(t.CloseTimeout):
		}
		if t.conn != nil {
			t.conn.Close()
		}
	}
}

func (t *TrapListener) SendUDP(packet *gosnmp.SnmpPacket, addr *net.UDPAddr) error {
	buf, err := packet.MarshalBinary()
	if err != nil {
		return err
	}
	_, err = t.conn.WriteToUDP(buf, addr)
	return err
}

func (t *TrapListener) listenUDP(addr string) error {
	udpAddr, err := net.ResolveUDPAddr(t.proto, addr)
	if err != nil {
		return err
	}
	conn, err := net.ListenUDP(t.proto, udpAddr)
	if err != nil {
		return err
	}
	t.conn = conn
	defer conn.Close()

	t.Mutex.Lock()
	t.listening <- true
	t.Mutex.Unlock()

	buffer := make([]byte, 65535)
	for {
		if atomic.LoadInt32(&t.finish) == 1 {
			break
		}
		n, remoteAddr, err := conn.ReadFromUDP(buffer)
		if err != nil {
			continue
		}
		packet := &gosnmp.SnmpPacket{}
		err = packet.UnmarshalBinary(buffer[:n])
		if err != nil {
			continue
		}

		if packet.Version == gosnmp.Version3 {
			if packet.SecModel != gosnmp.UserSecurityModel {
				t.Mutex.Lock()
				t.usmStatsUnknownEngineIDsCount++
				t.Mutex.Unlock()
				continue
			}
			if packet.SecEngineID != t.Params.SecurityEngineID {
				err := t.reportAuthoritativeEngineID(packet, t.Params.SecurityEngineID, remoteAddr)
				if err != nil {
					continue
				}
			}
		}

		if t.OnNewTrap != nil {
			t.OnNewTrap(packet, remoteAddr)
		}
	}
	return nil
}

func (t *TrapListener) reportAuthoritativeEngineID(trap *gosnmp.SnmpPacket, snmpEngineID string, addr *net.UDPAddr) error {
	report := &gosnmp.SnmpPacket{
		Version:      trap.Version,
		Community:    trap.Community,
		PDU: &gosnmp.PDU{
			Type: gosnmp.GetRequestPDU,
			Variables: []gosnmp.VarBind{
				{
					Name:  gosnmp.SnmpEngineID,
					Value: snmpEngineID,
				},
			},
		},
	}
	return t.SendUDP(report, addr)
}

