	p.Director(r, headers)

	backendConn, resp, err := p.Dialer.Dial(backendURL.String(), headers)
	if err != nil {
		http.Error(w, "Failed to connect to backend", http.StatusInternalServerError)
		return
	}
	defer backendConn.Close()

	clientConn, err := p.Upgrader.Upgrade(w, r, nil)
	if err != nil {
		http.Error(w, "Failed to upgrade client connection", http.StatusInternalServerError)
		return
	}
	defer clientConn.Close()

	errChan := make(chan error, 2)
	go p.replicateWebsocketConn(clientConn, backendConn, errChan)
	go p.replicateWebsocketConn(backendConn, clientConn, errChan)

	for i := 0; i < 2; i++ {
		if err := <-errChan; err != nil {
			// Handle error, connections are closed via defer
		}
	}
}

func (p *WebsocketProxy) replicateWebsocketConn(src, dst *websocket.Conn, errChan chan error) {
	for {
		_, msg, err := src.ReadMessage()
		if err != nil {
			errChan <- err
			return
		}
		if err := dst.WriteMessage(websocket.TextMessage, msg); err != nil {
			errChan <- err
			return
