// spawnUserNamespaceCat spawns a child process in a new user namespace to read a specified file.
// It uses pipes for inter-process communication to capture both standard output and error streams.
func spawnUserNamespaceCat(nsPath, path string) ([]byte, error) {
	// Create pipes for stdout and stderr
	stdoutPipeR, stdoutPipeW, err := os.Pipe()
	if err != nil {
		return nil, err
	}
	defer stdoutPipeR.Close()
	defer stdoutPipeW.Close()

	stderrPipeR, stderrPipeW, err := os.Pipe()
	if err != nil {
		return nil, err
	}
	defer stderrPipeR.Close()
	defer stderrPipeW.Close()

	// Prepare arguments for the external C function
	args := []string{nsPath, path}

	// Create the command
	cmd := exec.Command("spawn_userns_cat", args...)
	cmd.Stdout = stdoutPipeW
	cmd.Stderr = stderrPipeW

	// Start the process
	if err := cmd.Start(); err != nil {
		return nil, err
	}

	// Close the write ends of the pipes after the process starts
	stdoutPipeW.Close()
	stderrPipeW.Close()

	// Read stdout
	stdoutBuf := &bytes.Buffer{}
	if _, err := bufio.NewReader(stdoutPipeR).ReadBytes('\x00'); err != nil {
		return nil, err
	}
	if _, err := stdoutBuf.ReadFrom(stdoutPipeR); err != nil && err != os.EOF {
		return nil, err
	}

	// Read stderr
	stderrBuf := &bytes.Buffer{}
	if _, err := bufio.NewReader(stderrPipeR).ReadBytes('\x00'); err != nil {
		return nil, err
	}
	if _, err := stderrBuf.ReadFrom(stderrPipeR); err != nil && err != os.EOF {
		return nil, err
	}

	// Wait for the process to finish
	if err := cmd.Wait(); err != nil {
		if exitErr, ok := err.(*exec.ExitError); ok {
			if exitErr.ExitCode() != 0 {
				return nil, fmt.Errorf("child process exited with error: %s", stderrBuf.String())
			}
		}
		return nil, err
	}

	return stdoutBuf.Bytes(), nil
}

