type proxyCommand struct {
	cmd *exec.Cmd
}

func (p *proxyCommand) Start() error {
	// Create a pipe for communication with the child process
	pipeReader, pipeWriter, err := os.Pipe()
	if err != nil {
		return err
	}

	// Set up the command with the pipe as its standard output
	p.cmd.Stdout = pipeWriter
	p.cmd.Stderr = pipeWriter

	// Start the child process
	if err := p.cmd.Start(); err != nil {
		pipeWriter.Close()
		return err
	}

	// Ensure the pipe writer is closed after the command completes
	defer pipeWriter.Close()

	// Set a timeout for initialization
	timeout := 5 * time.Second
	timer := time.AfterFunc(timeout, func() {
		p.cmd.Process.Kill()
		pipeReader.Close()
	})

	// Read from the pipe to monitor the child's initialization status
	buf := make([]byte, 1024)
	n, err := pipeReader.Read(buf)
	if err != nil && err != os.EOF {
		return err
	}

	// Check if the timeout occurred
	if n == 0 {
		return errors.New("child process initialization timeout")
	}

	// Stop the timer if initialization was successful
	timer.Stop()

	return nil
}

