package main

import (
	"io"
	"os"
)

type fileCloner struct {
	writer  *os.File
	logFile *os.File
}

func (fc *fileCloner) Write(p []byte) (int, error) {
	n1, err1 := fc.writer.Write(p)
	n2, err2 := fc.logFile.Write(p)
	if err1 != nil {
		return n1, err1
	}
	if err2 != nil {
		return n2, err2
	}
	return n1, nil
}

type logRetriever struct {
	reader *os.File
	writer *os.File
	output *fileCloner
}

func newlogRetriever(logFile string) (*logRetriever, error) {
	reader, writer, err := os.Pipe()
	if err != nil {
		return nil, err
	}

	logFileFd, err := os.OpenFile(logFile, os.O_APPEND|os.O_WRONLY|os.O_CREATE, 0644)
	if err != nil {
		reader.Close()
		writer.Close()
		return nil, err
	}

	fc := &fileCloner{
		writer:  writer,
		logFile: logFileFd,
	}

	if _, err := os.Stat(logFile); !os.IsNotExist(err) {
		file, err := os.Open(logFile)
		if err != nil {
			reader.Close()
			writer.Close()
			logFileFd.Close()
			return nil, err
		}
		defer file.Close()
		if _, err := io.Copy(writer, file); err != nil {
			reader.Close()
			writer.Close()
			logFileFd.Close()
			return nil, err
		}
	}

	return &logRetriever{
		reader: reader,
		writer: writer,
		output: fc,
	}, nil
}

func (lr *logRetriever) Read(p []byte) (int, error) {
	return lr.reader.Read(p)
}

func (lr *logRetriever) CloseWriter() error {
	return lr.writer.Close()
}

func (lr *logRetriever) Close() error {
	return lr.reader.Close()
}

func (lr *logRetriever) Opt() cio.Opt {
	return cio.Opt{
		Reader:  os.Stdin,
		Output:  lr.output,
	}
}

