type LinkedHashSetINT64 struct {
	items map[int64]bool
	mu    sync.RWMutex
}

func (s *LinkedHashSetINT64) Add(id int64) {
	s.mu.Lock()
	defer s.mu.Unlock()
	s.items[id] = true
}

func (s *LinkedHashSetINT64) Iter() []int64 {
	s.mu.RLock()
	defer s.mu.RUnlock()
	var ids []int64
	for id := range s.items {
		ids = append(ids, id)
	}
	return ids
}

