	}

	w.Header().Set("Content-Type", "application/json")
	fmt.Fprintf(w, `{"status": "healthy", "last_update": "%s"}`, c.lastUpdate.Format(time.RFC3339))
}

func (c *Chart) handleData(w http.ResponseWriter, r *http.Request) {
	c.mutex.Lock()
	defer c.mutex.Unlock()

	pair := r.URL.Query().Get("pair")
	if pair == "" {
		http.Error(w, "Missing pair parameter", http.StatusBadRequest)
		return
	}

	candles, ok := c.candles[pair]
	if !ok {
		http.Error(w, "Pair not found", http.StatusNotFound)
		return
	}

	df, ok := c.dataframe[pair]
	if !ok {
		http.Error(w, "Dataframe not found", http.StatusNotFound)
		return
	}

	assetValue := c.paperWallet.AssetValue
	equityValue := c.paperWallet.EquityValue

	w.Header().Set("Content-Type", "application/json")
	fmt.Fprintf(w, `{"candles": %v, "asset_value": %f, "equity_value": %f, "dataframe": %v}`,
		candles, assetValue, equityValue, df)
}

func (c *Chart) handleTradingHistoryData(w http.ResponseWriter, r *http.Request) {
	c.mutex.Lock()
	defer c.mutex.Unlock()

	pair := r.URL.Query().Get("pair")
	if pair == "" {
		http.Error(w, "Missing pair parameter", http.StatusBadRequest)
		return
	}

	ordersSet, ok := c.ordersIDsByPair[pair]
	if !ok {
		http.Error(w, "No orders found for pair", http.StatusNotFound)
		return
	}

	var orderIDs []int64
	for _, id := range ordersSet.Iter() {
		orderIDs = append(orderIDs, id)
	}
	sort.Slice(orderIDs, func(i, j int) bool { return orderIDs[i] < orderIDs[j] })

	w.Header().Set("Content-Type", "text/csv")
	w.Header().Set("Content-Disposition", "attachment; filename=trading_history.csv")

	writer := csv.NewWriter(w)
	writer.Write([]string{"OrderID", "Pair", "Type", "Price", "Quantity", "Timestamp"})
	for _, id := range orderIDs {
		order, ok := c.orderByID[id]
		if !ok {
			continue
		}
		writer.Write([]string{
			fmt.Sprintf("%d", order.ID),
			order.Pair,
			order.Type,
			fmt.Sprintf("%f", order.Price),
			fmt.Sprintf("%f", order.Quantity),
			order.Timestamp.Format(time.RFC3339),
		})
	}
	writer.Flush()
}

func (c *Chart) Start() error {
	http.HandleFunc("/health", c.handleHealth)
	http.HandleFunc("/data", c.handleData)
	http.HandleFunc("/history", c.handleTradingHistoryData)

	return http.ListenAndServe(fmt.Sprintf(":%d", c.port), nil)
}

