package main

import (
	"fmt"
	"log"
	"net/http"
	"sync"
)

var (
	responses = make(map[string]*http.Response)
	response  *http.Response
	mu        sync.Mutex
)

func mainHandler(w http.ResponseWriter, r *http.Request) {
	proxifyHeader := r.Header.Get("proxify")
	if proxifyHeader == "" {
		http.Error(w, "Missing 'proxify' header", http.StatusBadRequest)
		return
	}

	mu.Lock()
	resp, ok := responses[proxifyHeader]
	mu.Unlock()

	if !ok {
		http.Error(w, "No response found for 'proxify' header", http.StatusNotFound)
		return
	}

	mu.Lock()
	delete(responses, proxifyHeader)
	mu.Unlock()

	for k, v := range resp.Header {
		w.Header()[k] = v
	}
	w.WriteHeader(resp.StatusCode)
	if _, err := w.Write(resp.Body.Bytes()); err != nil {
		log.Fatalf("Failed to write response: %v", err)
	}
}

func main() {
	http.HandleFunc("/", mainHandler)
	addr := options.HTTPListenerAddress
	fmt.Printf("Starting proxy server on %s\n", addr)
	if err := http.ListenAndServe(addr, nil); err != nil {
